.card {
    height: 405px;
    box-shadow: 0px 4px 30px rgba(52, 59, 76, 0.08);
    border-radius: 6px;
    display: block;
    overflow: hidden;
    // width: 32.02%;
    width: 383px;
    transition: .3s all ease;

    &:hover {
        box-shadow: 0px 4px 30px rgba(52, 59, 76, 0.13);
    }

    &__header {
        text-align: center;
        height: 178px;
        position: relative;
        background: url('../../../assets/uploads/card-bg-4.png'), linear-gradient(142.31deg, #FFBC77 6.92%, #FF8284 91.53%);
        background-repeat: no-repeat;
        background-position: center;
        background-size: cover;
    }

    &__hash {
        font-family: 'SFProDisplay-Regular';
        font-size: 15px;
        line-height: 22px;
        color: #FF6433;
        display: inline-block;
        padding-top: 18px;
    }

    &__title {
        position: absolute;
        left: 50%;
        top: 50%;
        transform: translate(-50%, -50%);
        font-family: 'SFProDisplay-Semibold';
        font-size: 33px;
        line-height: 36px;
        color: #FFFFFF;
    }

    &__body {
        padding: 32px 48px 42px 48px;
        text-align: center;
    }

    &__text {
        font-family: 'Roboto-Light';
        font-style: normal;
        font-weight: 300;
        font-size: 14px;
        line-height: 22px;
        color: #35343E;
        opacity: 0.7;
        margin-bottom: 22px;
    }

    &__link {
        width: 139px;
        height: 40px;
        background: transparent;
        border: 1px solid rgba(52, 59, 76, 0.2);
        box-sizing: border-box;
        border-radius: 100px;
        display: flex;
        align-items: center;
        justify-content: center;
        text-transform: uppercase;
        font-family: 'SFProDisplay-Regular';
        font-size: 10px;
        line-height: 19px;
        letter-spacing: 1px;
        color: #37393E;
        cursor: pointer;
        margin: 0 auto;
        transition: .3s all ease;

        &:hover {
            border: 1px solid rgba(52, 59, 76, 0.5);
        }
    }
}

@media (max-width: 960px) {
    .card {
        width: 48%;
    }
}

@media (max-width: 640px) {
    .card {
        height: 400px;
        width: 100%;

        &__header {
            height: 183px;
        }

        &__hash {
            padding-top: 21px;
        }

        &__title {
            margin-left: 0;
            font-size: 33px;
            line-height: 36px;
            padding-top: 13px;
        }

        &__body {
            padding-top: 30px;
        }

        &__text {
            max-width: 287px;
            letter-spacing: -0.2px;
        }
    }
}
