result:
.card {
    height: 405px;
    box-shadow: 0px 4px 30px rgba(52, 59, 76, 0.08);
    border-radius: 6px;
    display: block;
    overflow: hidden;
    // width: 32.02%;
    width: 383px;
    transition: .3s all ease;

    &:hover {
        box-shadow: 0px 4px 30px rgba(52, 59, 76, 0.13);
    }

    &__header {
        text-align: center;
        height: 178px;
        position: relative;
        margin-bottom: 34px;
    }

    &__hash {
        font-family: 'SFProDisplay-Regular';
        font-size: 15px;
        line-height: 22px;
        padding-top: 20px;
        display: inline-block;
        margin-left: 0;
        margin-bottom: 35px;
        color: #59DDCD;
    }

    &__title {
        margin-left: 0;
        margin-bottom: 0;
        position: absolute;
        left: 50%;
        top: 50%;
        transform: translate(-50%, -50%);
        font-family: 'SFProDisplay-Semibold';
        font-size: 33px;
        line-height: 36px;
        color: #FFFFFF;
    }

    &__body {
        padding: 0 42px 37px 42px;
    }

    &__choose {
        display: flex;
        align-items: center;
        justify-content: flex-start;
        margin-bottom: 20px;
        padding-bottom: 18px;
        position: relative;

        &:after {
            content: '';
            position: absolute;
            bottom: 0;
            right: 0;
            width: 75%;
            height: 1px;
            background: rgba(206, 219, 231, 0.26);
        }

        &:last-child {
            &:after {
                display: none;
            }
        }
    }

    &__img {
        margin-right: 15px;
    }

    &__text {
        align-self: flex-start;
    }

    &__type {
        font-family: 'Roboto-Light';
        font-style: normal;
        font-weight: normal;
        font-size: 12px;
        line-height: 23px;
        color: #37393E;
        opacity: 0.43;
        margin-bottom: 8px;
    }

    &__name {
        font-family: 'SFProDisplay-Regular';
        font-size: 15px;
        line-height: 20px;
        color: #37393E;
    }

    &__link {
        margin-left: auto;
        position: relative;
        cursor: pointer;

        &:hover {
            .progress-active {
                opacity: 1;

                &__circle {
                    stroke: #7FA3CA;
                    stroke-dasharray: 200;
                    animation: progress-btn .5s ease;
                }
            }
        }
    }
}

@media (max-width: 1200px) {
    .card {
        width: 305px;
    }
}

@media (max-width: 960px) {
    .card {
        width: 48%;
    }
}

@media (max-width: 640px) {
    .card {
        height: 400px;
        width: 100%;

        &__header {
            height: 182px;
            margin-bottom: 31px;
        }

        &__hash {
            padding-top: 18px;
        }

        &__body {
            padding: 0 33px 32px 33px;
        }

        &__choose {
            margin-bottom: 16px;
        }
    }
}
