.popup {
    position: fixed;
    z-index: 100;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    opacity: 0;
    pointer-events: none;
    transition: .3s all ease;
    background: rgba(247, 248, 250, 0.98);

    &.visible {
        opacity: 1;
        pointer-events: all;
    }

    &__close {
        position: fixed;
        top: 20px;
        right: 20px;
    }

    &__layot {
        position: absolute;
        top: 50%;
        left: 50%;
        transform: translate(-50%,-50%);
        max-width: 618px;
        // height: 59%;
        background: #FFFFFF;
        box-shadow: 0px 4px 30px rgba(52, 59, 76, 0.05);
        border-radius: 6px;
        padding: 65px;
    }

    &__title {
        font-family: 'SFProDisplay-Bold';
        font-size: 23px;
        line-height: 36px;
        text-align: center;
        color: #37393E;
        margin-bottom: 20px;
    }
}

#form {

}

input[type="text"],
input[type="email"] {
    display: block;
    width: 100%;
    margin-bottom: 30px;
    font-family: 'GothamPro-Regular';
    font-size: 10px;
    line-height: 10px;
    text-transform: uppercase;
    color: rgba(55, 57, 62, 0.7);
    padding: 12px 0;
    border-bottom: solid 1px rgba(54,54,54,0.15);
}

label[for="textarea"] {
    display: block;
    font-family: 'GothamPro-Regular';
    font-size: 8px;
    line-height: 8px;
    text-transform: uppercase;
    color: rgba(55, 57, 62, 0.7);
}

#textarea {
    width: 100%;
    display: block;
    padding: 19px 0;
    font-family: 'GothamPro-Regular';
    font-size: 11px;
    line-height: 17px;
    color: rgba(55, 57, 62, 0.7);
    border: none;
    border-bottom: solid 1px rgba(54,54,54,0.15);
    overflow-y: auto;
    max-height: 92px;
    margin-bottom: 35px;
}

button[type="submit"] {
    width: 146px;
    height: 43px;
    background: linear-gradient(0deg, #357FDB, #357FDB), linear-gradient(189.87deg, #357FDB -14.01%, #2361AA 109.14%);
    border-radius: 100px;
    display: flex;
    align-items: center;
    justify-content: center;
    font-family: 'SFProDisplay-Regular';
    font-size: 11px;
    line-height: 19px;
    text-align: center;
    letter-spacing: 1px;
    text-transform: uppercase;
    color: #FFFFFF;
    cursor: pointer;
    margin: 0 auto;
}
