.block {
    max-width: 676px;
    margin-bottom: 50px;

    &__table-outer {
        position: relative;

        &:before {
            content: '';
            position: absolute;
            z-index: 2;
            top: 0;
            right: 0;
            height: 100%;
            width: 50px;
            background: linear-gradient(to right, transparent 0%, #F8FAFF 100%);
            pointer-events: none;
            transition: all .5s ease;
            opacity: 0;
        }

        &.shadow {
            position: relative;
            z-index: 1;

            &:before {
               opacity: 1;
            }
        }
    }

    &__table {
        overflow-x: scroll;
        width: 100%;
        border: 1px solid #E9EEF7;
        box-sizing: border-box;
        border-radius: 6px;
        margin-top: 55px;
        position: relative;

        &::-webkit-scrollbar{
            height:3px;
            position: relative;
            z-index: 3;
        }

        &::-webkit-scrollbar-track{
            border-radius: 5px;
            background: #EBF2FD;
            position: relative;
            z-index: 3;
        }

        &::-webkit-scrollbar-thumb{
            background: rgba(53, 127, 219, 0.42);
            border-radius: 5px;
            position: relative;
            z-index: 3;
        }
    }
}

.table {
    border-collapse: collapse;

    th {
        font-family: 'SFProDisplay-Semibold';
        font-size: 16px;
        line-height: 19px;
        color: #357FDB;
        min-width: 153px;
        text-align: left;
        padding-left: 50px;


        &:first-child {
            min-width: 184px;
            text-align: left;
            padding-left: 34px;
        }
    }

    tr {

        &:first-child {
            height: 58px;
            background: linear-gradient(116.65deg, #F2F6FF 10.55%, #E4EDFD 107.49%), rgb(74, 117, 216) !important;
        }

        &:nth-child(odd) {
            td:first-child {
                background: #F5F8FF;
            }
        }

        &:nth-child(odd) {
            background: #F5F8FF;
        }

        td:first-child {
            position: sticky;
            z-index: 2;
            left: 0;
            top: auto;
            background: #fff;
            opacity: 1;

            &:before {
                content: '';
                position: absolute;
                top: 0;
                right: 0;
                height: 100%;
                width: 1px;
                background: #E9EEF7;
            }
        }

        th:first-child {
            position: sticky;
            z-index: 2;
            left: 0;
            top: auto;
            opacity: 1;
            background:#F2F6FF;

            &:before {
                content: '';
                position: absolute;
                top: 0;
                right: 0;
                height: 100%;
                width: 1px;
                background: #E9EEF7;
            }
        }
    }

    td {
        padding: 19px 0;
        text-align: center;
        font-family: 'Roboto-Regular';
        font-style: normal;
        font-weight: 300;
        font-size: 14px;
        line-height: 22px;
        color: #35343E;
        opacity: 0.7;
        min-width: 153px;

        &:first-child {
            min-width: 184px;
            font-family: 'SFProDisplay-Regular';
            font-size: 14px;
            line-height: 19px;
            color: #37393E;
            opacity: 0.75;
        }
    }
}

@media (max-width: 1200px) {
    .block {
        max-width: 600px;
    }
}

@media (max-width: 960px) {
    .block {
        max-width: 100%;
    }
}

@media (max-width: 640px) {
    .block {
        margin-bottom: 31px;

        &__table-outer {
            width: 106%;
        }

        &__table {
            margin-top: 40px;
        }
    }
}
