result:
.carousel {
    position: relative;

    &-buttons {
        position: absolute;
        z-index: 3;
        bottom: 21px;
        left: 38px;
        display: flex;

        & > * {
            margin-right: 10px;
        }
    }

    &-btn {
        position: relative;
        width: 53px;
        height: 53px;

        &:hover {
            .progress-active {
                opacity: 1;

                &__circle {
                    stroke: #7FA3CA;
                    stroke-dasharray: 200;
                    animation: progress-btn .5s ease;
                }
            }
        }

        &--next {
            margin-bottom: 6px;
        }

        &--prev {
            margin-right: 9px;
            margin-bottom: 6px;
        }

        .progress circle {
            stroke: #fff;
            opacity: 0.23;
        }

        .progress-active .progress-active__circle {
            stroke: #fff;
            opacity: 0.6;
        }

        .progress-arrow path {
            stroke: #fff;
            fill: #fff;
            opacity: 0.6;
        }
    }

    &-wrapper {
        position: relative;
        width: 100%;
        height: 100%;
    }

    &-slide {
        position: absolute;
        z-index: 1;
        top: 0; 
        left: 0;
        border-radius: 6px 0px 0px 6px;
        opacity: 0;
        width: 100%;
        height: 100%;
        background-position: center;
        background-size: cover;
        transition: .6s all ease;
        transform: translate3d(-20%, 0, 1px) scale(0.8);

        &.current {
            transform: translate3d(0, 0, 0) scale(1);
            opacity: 1;
            z-index: 3;
            transition: .3s all ease;
        }

        &.next {
            transform: translate3d(-8%, 0, 1px) scale(0.9);
            opacity: 0.1;
            z-index: 2;
        }

        &.latest {
            transform: translate3d(8%, 0, 1px) scale(0.9);
            opacity: 0.1;
            z-index: 1;
        }

        &.change-slide {
            transform: translate3d(100%, 0, 0);
            opacity: 0;
        }
    }

    /deep/ &-pagination {
        display: none;
        align-items: center;
        position: absolute;
        left: 50%;
        transform: translateX(-50%);
        bottom: 25px;

        & > * {
            &:not(:last-child) {
                margin-right: 13px;
            }
        }
    }

    /deep/ &-dot {
        display: inline-block;
        width: 6px;
        height: 6px;
        border-radius: 50%;
        background: #90B8EA;
        opacity: 0.4;
        cursor: pointer;

        &.active {
            position: relative;
            background: #618DC5;
            opacity: 1;

            &:after {
                content: '';
                position: absolute;
                top: 50%;
                left: 50%;
                transform: translate(-50%,-50%);
                width: 14px;
                height: 14px;
                border-radius: 50%;
                background: rgba(144, 184, 234, 0.25);
            }
        }
    }
}

@media (max-width: 960px) {
    .carousel {


        &-pagination {
            display: flex;
            bottom: -30px;
        }

        &-slide {
            border-radius: 6px;
        }
    }
}

@media (max-width: 640px) {
    .carousel {


        &-buttons {
            display: none;
        }

        &-slide {
            &.next {
                transform: translate3d(-12%, 0, 1px) scale(0.85);
            }
        }

        &-pagination {
            bottom: -27px;
        }
    }
}
