.card {
    background: linear-gradient(134.98deg, #3B7FD4 7.03%, #0952A7 140.8%);
    background-repeat: no-repeat;
    background-position: center;
    background-size: cover;
    height: 331px;
    width: 353px;
    box-shadow: 0px 4px 30px rgba(52, 59, 76, 0.08);
    border-radius: 6px;
    display: inline-block;
    overflow: hidden;
    position: relative;
    padding: 62px 0;
    text-align: center;
    transition: .3s all ease;
    color: #fff;

    &:hover {
        box-shadow: 0px 4px 30px rgba(52, 59, 76, 0.13);
    }

    &__title {
        font-family: 'SFProDisplay-Semibold';
        font-size: 30px;
        line-height: 36px;
        max-width: 218px;
        margin: 0 auto;
    }

    &__link {
        width: 139px;
        height: 40px;
        background: #FFFFFF;
        box-sizing: border-box;
        border-radius: 100px;
        display: flex;
        align-items: center;
        justify-content: center;
        text-transform: uppercase;
        font-family: 'SFProDisplay-Semibold';
        font-size: 10px;
        line-height: 19px;
        letter-spacing: 1px;
        color: #37393E;
        cursor: pointer;
        transition: .3s all ease;
        position: absolute;
        left: 50%;
        bottom: 62px;
        transform: translateX(-50%);
        // margin: 95px auto 0 auto;

        span {
            position: relative;
            z-index: 2;
        }

        &:hover:after {
            opacity: 1;
        }

        &:after {
            content: '';
            width: 100%;
            height: 100%;
            border-radius: 100px;
            border: none;
            position: absolute;
            top: 0;
            left: 0;
            opacity: 0;
            background: linear-gradient(0deg, rgba(255, 255, 255, 0.85), rgba(255, 255, 255, 0.85)), linear-gradient(189.87deg, #357FDB -14.01%, #2361AA 109.14%);
            transition: .3s all ease;
        }
    }
}

@media (max-width: 1200px) {
    .card {
        width: 300px;
    }
}

@media (max-width: 960px) {
    .card {
        height: 415px;
        width: 47.5%;
    }
}

@media (max-width: 640px) {
    .card {
        height: 337px;
        padding-top: 77px;

        &__link {
            bottom: 78px;
        }
    }
}
