.preloader {
	position: absolute;
	top: 0;
	left: 0;
	z-index: 10;
	font-size: 0;
	pointer-events: none;
	min-height: 100vh;
	background-color: #fff;

	img {
		width: 100%;
		display: block;
	}

	&_hidden {
		opacity: 0;
		transition: opacity .5s ease;
	}

	&_2 {
		top: 100%;
	}
}

@media screen and (max-width: 960px) {
	.preloader {
		display: none !important;
	}
}
