.awards {
    padding: 0 0 123px 0;
    background-repeat: no-repeat;
    background-size: 30%;
    background-position: right -20% top 0%;
    position: relative;

    &:before {
        content: url('../../assets/img/winners-bg.png');
        position: absolute;
        z-index: 0;
        right: -20%;
        top: 0;
        opacity: 0.3;
        max-width: 733px;
        max-height: 863px;
    }

    &__title {
        font-family: 'SFProDisplay';
        font-weight: 700;
        font-size: 36px;
        line-height: normal;
        text-align: center;
        color: #FF8284;
        margin-bottom: 13px;
        text-align: center;
    }

    &__subtitle {
        font-family: 'Roboto';
        font-weight: 300;
        font-style: normal;
        font-weight: 300;
        font-size: 15px;
        line-height: 25px;
        text-align: center;
        color: #37393E;
        opacity: 0.7;
        margin: 0 auto 51px auto;
        text-align: center;
        max-width: 549px;
    }

    &__list {
        display: grid;
        grid-column-gap: 23px;
        grid-row-gap: 23px;
        grid-template-columns: repeat(3, 1fr);
    }

    &.application {
        background-position: -10% 0%;
        padding-bottom: 95px;

        &:before {
                left: -20% !important;
            }

        .awards {
            padding-bottom: 109px;

        }

        .awards__title {
            color: #357FDB;
        }
    }
}

@media(max-width: 960px) {
    .awards {
        padding-top: 70px;
        padding-bottom: 100px;

        &__list {
            grid-template-columns: repeat(2, auto);
        }
    }
}

@media(max-width: 640px) {
    .awards {
        padding-bottom: 39px;
        padding-top: 40px;

        &:before {
            display: none;
        }

        &__title {
            font-size: 32px;
            line-height: normal;
            text-align: center;
            margin-bottom: 5px;
        }

        &__subtitle {
            max-width: 344px;
            margin-right: auto;
            margin-left: auto;
            margin-bottom: 32px;
        }

        &__list {
            grid-template-columns: auto;
            grid-row-gap: 20px;
        }

        &.application {
            padding-bottom: 46px;
        }
    }
}
