result:
.card {
    display: block;
    width: 383px;
    height: 307px;
    background: #FFFFFF;
    box-shadow: 0px 4px 33px rgba(52, 59, 76, 0.09);
    border-radius: 6px;
    padding: 25px 33px 34px 33px;
    position: relative;
    transition: .3s all ease;

    &:hover {
        box-shadow: 0px 4px 30px rgba(52, 59, 76, 0.13);
    }

    &__hash {
        font-family: 'Roboto-Regular';
        font-style: normal;
        font-weight: normal;
        font-size: 12px;
        line-height: 23px;
        text-align: center;
        color: #37393E;
        opacity: 0.43;
        display: block;
        margin: 0 auto -12px auto;
        text-align: center;
    }

    &__title {
        font-family: 'SFProDisplay-Semibold';
        font-size: 23px;
        line-height: 48px;
        text-align: center;
        color: #37393E;
        margin: 0 auto 15px auto;
        letter-spacing: -0.5px;
    }

    &__content {
        border-top: 1px solid rgba(206, 219, 231, 0.26);
        padding-top: 24px;
        display: flex;
    }

    &__description {
        margin-top: -14px;
    }

    &__name {
        font-family: 'SFProDisplay-Semibold';
        font-size: 16px;
        line-height: 48px;
        color: #FF8284;
        letter-spacing: -0.5px;
        margin-bottom: -7px;
    }

    &__text {
        font-family: 'Roboto-Light';
        font-style: normal;
        font-weight: 300;
        font-size: 12px;
        line-height: 20px;
        color: #35343E;
        opacity: 0.7;
    }

    &__avatar {
        margin-right: 20px;
        width: 72px;
        height: 72px;
        flex-shrink: 0;
        overflow: hidden;

        img {
            width: 100%;
            height: 100%;
        }
    }

    &__link {
        position: absolute;
        left: 50%;
        transform: translateX(-50%);
        bottom: 34px;
        width: 139px;
        height: 40px;
        border: 1px solid rgba(52, 59, 76, 0.2);
        box-sizing: border-box;
        border-radius: 100px;
        display: flex;
        align-items: center;
        justify-content: center;
        font-family: 'SFProDisplay-Regular';
        font-size: 10px;
        line-height: 19px;
        text-align: center;
        letter-spacing: 1px;
        text-transform: uppercase;
        color: #37393E;
        transition: .3s all ease;

        &:hover {
            border: 1px solid rgba(52, 59, 76, 0.5);
        }
    }

    &.application {
        .card__name {
            color: #357FDB;
        }
    }
}

@media (max-width: 1200px) {
    .card {
        width: 304px;
        padding: 25px 33px;

        &__title {
            margin-bottom: 0;
        }

        &__link {
            bottom: 25px;
        }
    }
}

@media (max-width: 960px) {
    .card {
        width: 100%;
    }
}

@media (max-width: 640px) {
    .card {
        width: 100%;
        padding: 25px 27px;

        &__title {
            margin-bottom: 15px;
        }

        &__link {
            bottom: 34px;
        }
    }
}
