result:
    .carousel {
        display: none;
    }

    .block.text {
        margin-top: 77px;
    }

    .poster {
        padding-top: 141px;
        padding-bottom: 33px;
        // background: linear-gradient(180deg, #EAF1FC -4.24%, #F8FAFF 86.01%), url('../../assets/img/application-poster-bg.png');
        background: url('../../assets/img/main-bg-pattern.png'),
                    linear-gradient(180deg, #EAF1FC -4.24%, #F8FAFF 86.01%);
        background-repeat: no-repeat;
        background-size: 30%, 100%;
        background-position: right top, center;
        position: relative;
        margin: 10px 10px 0 10px;
        border-radius: 8px 8px 0 0 ;
        overflow: hidden;

        &:before {
            content: '';
            width: 347px;
            height: 347px;
            background: #9E7DFD;
            mix-blend-mode: overlay;
            filter: blur(150px);
            position: absolute;
            left: 10.7%;
            top: -146px;
        }

        &__title {
            font-family: 'SFProDisplay';
font-weight: 700;
            font-size: 45px;
            line-height: 59px;
            text-align: center;
            letter-spacing: 1px;
            text-transform: uppercase;
            color: #3D4950;
            max-width: 659px;
            margin: 0 auto 133px auto;
            position: relative;

            span {
                color: #357FDB;
                position: relative;
                z-index: 2;
                display: inline-block;

                &:after {
                    content: '';
                    position: absolute;
                    z-index: -1;
                    bottom: 0;
                    left: 0;
                    width: 100%;
                    height: 23px;
                    background: #F8FAFE;
                    opacity: 0.8;
                    border-radius: 3px;
                }
            }
        }
    }

    .gallery {

        & > * {
            &:not(:last-child) {
                margin-right: 23px;
            }
        }

        &__item {
            display: inline-block;
            width: 32%;
            position: relative;
            padding-bottom: 41%;
            background-repeat: no-repeat;
            background-position: center;
            background-size: cover;

            &.offset {
                position: relative;
                bottom: 30px;
            }

            img {
                position: absolute;
                top: 0;
                left: 0;
                width: 100%;
                height: 100%;
            }
        }
    }

    .block {
        margin-left: auto;
        margin-right: auto;
        margin-top: 84px;
    }

    @media (max-width: 1200px) {
        .poster {

        }

        .gallery {
            & > * {
                &:not(:last-child) {
                    margin-right: 18px;
                }
            }
        }
    }

    @media (max-width: 960px) {
        .carousel {
            display: block;
            position: relative;
            top: auto;
            left: auto;
            width: 80%;
            margin-right: auto;
            margin-left: auto;
            height: 375px;
        }

        .gallery {
            display: none;
        }

        .poster {
            &__title {
                margin-bottom: 80px;
                font-size: 40px;
                max-width: 500px;
                margin-left: auto;
                margin-right: auto;
            }
        }

        .block.text {
            margin-top: 90px;
        }
    }

    @media (max-width: 640px) {
        .carousel {
            width: 96%;
            height: 230px;
        }

        .block.text {
            margin-top: 68px;
            max-width: 344px;
        }

        .poster {
            padding-top: 109px;
            padding-bottom: 10px;

            &__title {
                max-width: 304px;
                margin-left: auto;
                margin-right: auto;
                font-size: 36px;
                line-height: 43px;
                margin-bottom: 55px;
            }
        }
    }
