.award {
    width: 267px;
    height: 153px;
    background-color: #fff;
    box-shadow: 0px 4px 30px rgba(255, 150, 127, 0.12);
    border-radius: 6px;
    position: relative;
    padding-top: 73px;
    text-align: center;
    // background-image: url('../../../assets/img/award-card-bg.png');
    background-repeat: no-repeat;
    background-position: center;
    background-size: contain;
    margin: 0 auto 35px auto;
    -webkit-backface-visibility: hidden;
    overflow: visible !important;

    &__icon {
        width: 88px;
        height: 88px;
        border-radius: 50%;
        border: 15px solid #fff;
        box-sizing: border-box;
        // background: linear-gradient(147.83deg, #FF8284 2.55%, #FFBC77 207.56%), #C4C4C4;
        display: flex;
        align-items: center;
        justify-content: center;
        position: absolute;
        z-index: 2;
        left: 50%;
        top: -23px;
        transform: translateX(-50%);
        -webkit-backface-visibility: hidden;

        img {
            max-width: 25px;
            max-height: 25px;
        }
    }

    &__separator {
        width: 1px;
        height: 23px;
        background: rgba(255, 130, 132, 0.26);
        margin: 0 auto 0 auto;
    }

    &__signature {
        font-family: 'Roboto';
        font-weight: normal;
        font-style: normal;
        font-weight: normal;
        font-size: 12px;
        line-height: 23px;
        text-align: center;
        color: #37393E;
        opacity: 0.43;
        margin-bottom: 6px;
    }

    &__date {
        font-family: 'SFProDisplay';
        font-weight: normal;
        font-size: 15px;
        line-height: 19px;
        text-align: center;
        color: #37393E;
    }

    &.application {
        &.award {
            // box-shadow: 0px 4px 30px rgba(131,165,252,0.12);
            // background-image: url('../../../assets/img/award-card-application-bg.png');
        }

        .award__icon {
            // background: linear-gradient(145.3deg, #83A5FC 2.7%, #3182E7 109.91%), #C4C4C4;
            // border: 15px solid #E8F0FF;
        }

        .award__separator {
            // background: rgba(86, 146, 240, 0.37);
        }
    }
}

@media (max-width: 640px) {
    .award {
        width: 174px;
        height: 164px;
        background-color: transparent;
        box-shadow: none !important;
        padding-top: 4px;
        margin-bottom: 3px;

        &__icon {
            position: relative;
            left: 50%;
            transform: translateX(-50%);
            top: auto;
            width: 76px;
            height: 76px;
            border: 13px solid #FADCD9;
        }

        &__separator {
            margin-top: 10px;
        }

        &__date {
            line-height: 13px;
        }

        &.application {
            padding-top: 14px;
            margin-bottom: 15px;
        }
    }
}
