result:
	textarea[name="message"] {
        padding: 0 !important;
    }

	.ui-textarea {
		width: 100%;
		position: relative;
		margin-bottom: 31px;
		border-bottom: solid 1px rgba(54,54,54,0.15);
		transition: border-color .5s ease;

		&--light {
			border-bottom: 1px solid rgba(255,255,255,0.18);

			.ui-textarea__name {
				color: #fff;
			}

			textarea {
				color: #fff !important;
			}
		}

		&__name {
			font-family: "GothamPro-Regular";
			font-size: 10px;
            line-height: 10px;
            text-transform: uppercase;
            color: rgba(55, 57, 62, 0.7);
			position: absolute;
			left: 0px;
			top: 0px;
			transform: translateX(-1px) translateY(5px);
			transform-origin: 0 0;
			pointer-events: none;
			transition: all 0.3s ease;

		}

		&__error-label {
			position: absolute;
			bottom: -25px;
			left: -1px;
			font-family: 'Roboto-Light';
            font-style: normal;
            font-weight: 300;
            font-size: 10px;
            line-height: 22px;
			color: #D93737;
			opacity: 0;
			pointer-events: none;
			transition: opacity .5s ease;
		}

		textarea {
			width: 100%;
			background: transparent;
			padding-bottom: 9px;
			font-size: 10px;
            line-height: 10px;
            text-transform: uppercase;
            color: rgba(55, 57, 62, 0.7);
            border: none;

			&:focus {
                outline: none;

				& + .ui-textarea {
					&__name {
						transform: translateX(0px) translateY(-15px) scale(0.7);
						transform-origin: 0 0;
					}
				}
			}
		}

		// &:hover {
		// 	border-color: rgba(#2D3D65, .5);
		// 	.ui-input {
		// 		&__name {
		// 			color: #070B14;
		// 		}
		// 	}
		// }

		&_active {
			.ui-textarea {
				&__name {
					transform: translateX(0px) translateY(-15px) scale(0.7);
					transform-origin: 0 0;
				}
			}
		}

		&_error {
			.ui-textarea {
				&__name {
					color: #D93737;
				}
				&__error-label {
					opacity: 1;
				}
			}
			border-color: rgba( #D93737,.46);
		}
	}

	@media (max-width: 1200px) {
		.ui-textarea {
			margin-bottom: 36px;
			textarea {
				padding-bottom: 4px;
			}
			&__name {
				left: 1px;
				transform: translateX(-1px) translateY(0px);
			}
		}
	}

	@media (max-width: 640px) {
		.ui-textarea {
			margin-bottom: 6px;
			&__name {
				left: 3px;
				top: -5px;
			}
		}
	}
