    import vInput from '../../components/input';
    import vTextarea from '../../components/textarea';
    import { swiper, swiperSlide } from 'vue-awesome-swiper';

    import $api from '@/components/api.js';

    export default {
        name: 'Team',
        props: {
            page: Object
        },
        data: function(){
            return {
                modalVisible: false,
                textareaAutosize: '',
                formFirstName: "Name",
                formEmail: "E-mail",
                formMessage: "Your message",
                name: '',
                email: '',
                message: '',
                nameValid: true,
                emailValid: true,
                messageValid: true,
                thanksMessage: false,

                swiperOptions: {
                    spaceBetween: 10,
                    slidesPerView: 'auto',
                    loopedSlides: 1,
                    centeredSlides: true,
                    pagination: {
                        el: '.team__pagination',
                        type: 'bullets',
                        clickable: true
                    },
                    paginationClickable :true,
                    loop: true,
                },
            }
        },
        components: {
            'v-input': vInput,
            'v-textarea': vTextarea,
            'swiper': swiper,
            'swiperSlide': swiperSlide,
        },
        methods: {
            showModal(){
                this.modalVisible = !this.modalVisible;
            },
            checkMessage(val){
                this.message = val;
                if(val.length > 0) {
                    this.messageValid = true;
                }
            },
            isEmailValid(email) {
                let re = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
                return re.test(String(email).toLowerCase());
            },
            onSubmit(){
                let isValid = true,
                    formData = new FormData;

                if(!this.name) {
                    this.nameValid = false;
                    isValid= false;
                } else {
                    this.nameValid = true;
				    formData.append('your-name', this.name);
                }

                if(this.isEmailValid(this.email)) {
                    this.emailValid = true;
                    formData.append('your-email', this.email);
                } else {
                    this.emailValid = false;
                    isValid = false;
                }

                if(!this.message) {
                    this.messageValid = false;
                    isValid = false;
                } else {
                    this.messageValid = true;
				    formData.append('your-message', this.message);
                }

                if (!isValid) return;

                $api.sendFormExpert(formData, resp => {
                    if (resp.data.status == 'mail_sent') {
                        this.onSuccess();
                    }
                });
            },
            onSuccess(){
                this.$emit('showThanksBlock', true);
                this.modalVisible = false;
                // this.thanksMessage = true;
                // setTimeout(() => {
                //     this.thanksMessage = false;
                //     this.modalVisible = false;
                // }, 2000);
            }
        },
    }
