result:
.flex {
    display: flex;
    align-items: flex-start;
}

.description {
    position: relative;
    padding: 72px 0 54px 0;

    &__content {
        // display: inline-block;
    }

    &__sidebar {
        position: absolute;
        // padding-right: 40px;
        // margin: 70px -6.8% 0 auto;
        // margin-right: -81px;
        top: 142px;
        right: 81px;
        // display: inline-flex;
        display: flex;
        flex-direction: column;

        & > * {
            &:not(:last-child) {
                margin-bottom: 23px;
            }
        }
    }
}

#block-app {
    position: fixed;
    z-index: 10;
    right: -400px;
    bottom: 10px;
    transition: .3s all ease;
    width: 353px;
    height: 106px;
    padding: 26px 27px;
    background: #FFFFFF;
    border: 1px solid rgba(0, 0, 0, 0.02);
    box-sizing: border-box;
    box-shadow: 0px 4px 24px rgba(52, 59, 76, 0.04);
    border-radius: 6px;
}

@media (max-width: 1200px) {
    .description {
        &__sidebar {
            right: 30px;
            align-items: flex-end;
        }
    }
}

@media (max-width: 960px) {
    .description {
        padding-top: 40px;

        &__sidebar {
            position: relative;
            top: auto;
            right: auto;
            padding: 0 30px;
            display: flex;
            flex-direction: row;
            flex-wrap: wrap;

            & > * {

                &:nth-child(odd) {
                    margin-right: 5%;
                }

                &:last-child {
                    margin-right: 0;
                }
            }
        }
    }

    .social-block {
        margin-bottom: 40px;
        justify-content: center;
    }
}

@media (max-width: 640px) {
    .description {
        padding-top: 31px;
        padding-bottom: 13px;

        &__title {
            margin-bottom: 5px;
        }

        &__sidebar {
            flex-direction: column;
            top: -7px;
            padding: 0 20px;

            & > * {
                width: 100%;
                margin-right: 0;
                margin-bottom: 20px;

                &:nth-child(odd) {
                    margin-right: 0;
                }

                &:last-child {
                    margin-right: 0;
                }
            }
        }
    }
}
