.description {
	position: relative;
	padding: 72px 0 54px 0;

	.wrapper {
		display: flex;
		justify-content: space-between;
		flex-wrap: nowrap;
	}

	&__sidebar {
		display: inline-block;
		margin-right: -40px;
		width: 353px;

		& > * {
			&:not(:last-child) {
				margin-bottom: 23px !important;
			}
		}
	}
}

#block-app {
	position: fixed;
	z-index: 10;
	right: -400px;
	bottom: 10px;
	transition: .3s all ease;
	width: 353px;
	height: 106px;
	padding: 26px 27px;
	background: #FFFFFF;
	border: 1px solid rgba(0, 0, 0, 0.02);
	box-sizing: border-box;
	box-shadow: 0px 4px 24px rgba(52, 59, 76, 0.04);
	border-radius: 6px;
}

@media (max-width: 1285px) {
	.description {

		&__content {
			& > * {
				margin-right: 20px;
			}
		}

		&__sidebar {
			margin-right: 0;
		}
	}
}

@media (max-width: 1200px) {
	.description {
		&__sidebar {
			width: 300px;
		}
	}

	#block-app {
		visibility: hidden;
	}
}

@media (max-width: 960px) {
	.description {
		padding-top: 40px;

		.wrapper {
			display: block;
		}

		& > * {
			margin-right: 0;
		}

		&__sidebar {
			width: 100%;
			display: flex;
			flex-direction: row;
			flex-wrap: wrap;
			justify-content: space-between;

			& > * {
				margin-bottom: 23px !important;

				&:last-child {
					margin-right: 0;
				}
			}
		}
	}

	.social-block {
		margin-bottom: 40px;
		justify-content: center;
	}

	.description {

		&__content {
			& > * {
				margin-right: 0;
			}
		}
	}
}

@media (max-width: 640px) {



	.description {
		padding-top: 31px;
		padding-bottom: 13px;

		&__title {
			margin-bottom: 5px;
		}

		&__sidebar {

			& > * {
				width: 100%;
				margin-right: 0;
				margin-bottom: 20px;

				&:nth-child(odd) {
					margin-right: 0;
				}

				&:last-child {
					margin-right: 0;
				}
			}
		}
	}
}
