.card {
    width: 353px;
    height: 415px;
    box-shadow: 0px 4px 30px rgba(52, 59, 76, 0.08);
    border-radius: 6px;
    display: inline-block;
    overflow: hidden;
    position: relative;
    text-align: center;
    transition: .3s all ease;
    color: #fff;

    &:hover {
        box-shadow: 0px 4px 30px rgba(52, 59, 76, 0.13);
    }

    &__header {
        height: 131px;
        background-repeat: no-repeat;
        background-position: center;
        background-size: cover;
        position: relative;
    }

    &__title {
        display: block;
        position: absolute;
        top: calc(50% - 3px);
        left: 50%;
        transform: translate(-50%,-50%);
        font-family: 'SFProDisplay-Bold';
        font-size: 30px;
        line-height: 36px;
        // white-space: nowrap;
        width: 100%;
        padding: 0 10px;
    }

    &__body {
        padding: 26px;
    }

    &__article {
        display: flex;
        align-items: flex-start;
        margin-bottom: 25px;
        padding-bottom: 10px;
    }

    &__avatar {
        margin-right: 21px;
        width: 52px;
        height: 52px;
        overflow: hidden;
        display: flex;
        align-items: center;
        justify-content: center;

        img {
            width: 100%;
            height: 100%;
        }
    }

    &__id {
        color: #37393E;
        text-align: left;
        position: relative;
        bottom: 5px;
    }

    &__type {
        font-family: 'Roboto-Regular';
        font-style: normal;
        font-weight: normal;
        font-size: 12px;
        line-height: 23px;
        opacity: 0.43;
    }

    &__name {
        font-family: 'SFProDisplay-Regular';
        font-size: 15px;
        line-height: 19px;
    }
}

@media (max-width: 1200px) {
    .card {
        width: 300px;
        height: 435px;

        &__avatar {
            flex-shrink: 0;
        }
    }
}

@media (max-width: 960px) {
    .card {
        height: 415px;
        width: 47.5%;
    }
}

@media (max-width: 640px) {
    .card {
        height: 453px;

        &__avatar {
            width: 58px;
            height: 58px;
            margin-right: 15px;
        }

        &__body {
            padding-top: 36px;
        }
    }
}
