result:
.card {
    background-repeat: no-repeat;
    background-position: center;
    background-size: cover;
    height: 445px;
    width: 353px;
    box-shadow: 0px 4px 30px rgba(52, 59, 76, 0.08);
    border-radius: 6px;
    overflow: hidden;
    position: relative;
    padding: 23px 0 37px 0;
    text-align: center;
    transition: .3s all ease;
    color: #fff;

    &:hover {
        box-shadow: 0px 4px 30px rgba(52, 59, 76, 0.13);
    }

    &__hash {
        font-family: 'SFProDisplay-Regular';
        font-size: 15px;
        line-height: 22px;
        color: #59DDCD;
        display: inline-block;
        margin-bottom: 21px;
        color:#EF3044;
    }

    &__content {
        max-width: 90%;
        display: block;
        margin: 0 auto;
    }

    &__title {
        font-family: 'SFProDisplay-Semibold';
        margin-bottom: 18px;
        font-size: 30px;
        line-height: 36px;
        // letter-spacing: -0.6px;
    }

    &__desc {
        font-family: 'Roboto-Light';
        font-style: normal;
        font-weight: 300;
        font-size: 14px;
        line-height: 22px;
        margin-bottom: 25px;
        opacity: 0.9;
        margin-bottom: 45px;
    }

    &__icon {
        width: 42px;
        height: 42px;
        border-radius: 50%;
        background: linear-gradient(145.3deg, #E8AA6B 2.7%, #F55A43 109.91%), #C4C4C4;
        opacity: 1;
        display: flex;
        align-items: center;
        justify-content: center;
        position: relative;
        margin: 0 auto;

        &:after {
            content: '';
            width: 64px;
            height: 64px;
            border-radius: 50%;
            background: linear-gradient(145.3deg, #FFBC77 2.7%, #FE6C56 109.91%), #C4C4C4;
            opacity: 0.23;
            position: absolute;
            top: 50%;
            left: 50%;
            transform: translate(-50%,-50%);
        }
    }

    &__nomination {
        margin: 27px auto 29px auto;
        display: block;
        font-family: 'Roboto-Regular';
        font-style: normal;
        font-weight: 300;
        font-size: 12px;
        line-height: 17px;
        text-align: center;
        max-width: 109px;
    }

    &__link {
        width: 139px;
        height: 40px;
        box-sizing: border-box;
        border-radius: 100px;
        display: flex;
        align-items: center;
        justify-content: center;
        text-transform: uppercase;
        font-family: 'SFProDisplay-Semibold';
        font-size: 10px;
        line-height: 19px;
        letter-spacing: 1px;
        cursor: pointer;
        position: absolute;
        left: 50%;
        transform: translateX(-50%);
        bottom: 37px;
        background: transparent;
        border: 1px solid rgba(255, 255, 255, 0.51);
        color: #fff;
        transition: .3s all ease;

        &:hover {
            border: 1px solid #FFFBFB;
        }
    }
}

@media (max-width: 1200px) {
    .card {
        width: 300px;
    }
}

@media (max-width: 960px) {
    .card {
        height: 415px;
        width: 47.5%;
    }
}

@media (max-width: 640px) {
    .card {
        height: 445px;
        padding-top: 18px;

        &__hash {
            margin-bottom: 30px;
        }

        &__title {
            margin-bottom: 16px;
        }

        &__desc {
            margin-bottom: 39px;
        }

        &__nomination {
            margin-top: 25px;
        }
    }
}
