  import {mask} from 'vue-the-mask';
  import autosize from 'autosize';

  export default {
	name: 'v-textarea',
	data: function() {
	return {
			checkVal:false
		}
	},
	directives: {mask},
	props:{
		lightColor: {
			type: Boolean,
			default: false
		},
		value:{
			type:String,
			default:''
		},
		name:{
			type:String,
			default:''
		},
		label:{
			type:String,
			default:''
		},
		autocomplete: {
			type: String,
			default: ''
		},
		isValid: {
			type: Boolean,
			default: true
        },
        maxHeight: {
            type: Number,
            default: 0
        },
        minHeight: {
            type: Number,
            default: 0
        }
	},
	methods:{
        onChange(val){
            this.checkVal = val.length > 0;
            this.$emit('value', val);
		},
	},
	mounted() {
		this.checkVal = this.value !="" ? true : false;

		// autosize(document.getElementById('textarea'));
	},
	watch: {
		value(val) {
			this.checkVal = val !="" ? true : false;
		},
	}
  }
