result:
.form {
    padding-bottom: 97px;
}

.form-questions {
    background: linear-gradient(139.69deg, #357FDB -29.91%, #60D0E9 97.64%);
    border-radius: 6px;
    padding: 48px 0 57px 110px;
    color: #fff;
    overflow: hidden;
    position: relative;

    &__title {
        font-family: 'SFProDisplay-Bold';
        font-size: 40px;
        line-height: 46px;
        max-width: 534px;
        margin-bottom: 30px;
        letter-spacing: -1px;
    }

    .fieldset {
        &-name {
            width: 100%;
            max-width: 290px;
            display: inline-block;
            margin-right: 36px;
        }

        &-email {
            width: 100%;
            max-width: 290px;
            margin-bottom: 32px;
        }

        &-message {
            width: 100%;
            max-width: 290px;
            display: inline-block;
            position: relative;
            top: 6px;
        }
    }

    button[type="button"] {
        width: 139px;
        height: 40px;
        background: #fff;
        border-radius: 100px;
        display: flex;
        align-items: center;
        justify-content: center;
        font-family: 'SFProDisplay-Regular';
        font-size: 11px;
        line-height: 19px;
        text-align: center;
        letter-spacing: 1px;
        text-transform: uppercase;
        color: #37393E;
        cursor: pointer;
        position: relative;
        transition: .3s all ease;

        span {
            position: relative;
            z-index: 2;
        }

        &:after {
            content: '';
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            border-radius: 100px;
            background: linear-gradient(0deg, rgba(255, 255, 255, 0.85), rgba(255, 255, 255, 0.85)), linear-gradient(189.87deg, #357FDB -14.01%, #2361AA 109.14%);
            transition: .3s all ease;
            opacity: 0;
        }

        &:hover:after {
            opacity: 1;
        }
    }
}

.widget {
    width: 60%;
    height: 100%;
    position: absolute;
    z-index: 0;
    right: -16%;
    top: 0;

    &__wrapper {
        transform: scale(0.82) !important;
    }
}

@media (max-width: 1200px) {
    .form-questions {
        padding: 48px 0 57px 50px;
    }
}
