result:
    .poster {
        background-repeat: no-repeat;
        background-position: right top, center;
        background-size: 40%, cover;
        margin: 10px 10px 0 10px;

        &__top {
            background: url('../../assets/img/main-bg-pattern.png'),
                        linear-gradient(174.64deg, #EBF1FD -30.68%, #DAEEF6 187.69%);
            background-repeat: no-repeat;
            background-size: 30%, 100%;
            background-position: right top, center;
            padding: 0 0 32px 87px;
            padding-top: 90px;
            border-radius: 8px 8px 0 0;
            position: relative;
        }

        &__bottom {
            text-align: center;
            padding-bottom: 32px;

            img {
                width: 52%;
                height: auto;
                margin-top: 8px;
            }
        }

        &__scroll {
            margin: 0 auto;
            margin-top: 0;
            display: block;
            position: relative;
            z-index: 2;
        }

        &__text {
            margin-bottom: 10px;
            font-family: 'SFProDisplay';
font-weight: normal;
            font-size: 12px;
            line-height: 19px;
            letter-spacing: 1px;
            color: #3D4950;
            cursor: pointer;
            position: relative;
        }

        &__line {
            display: inline-block;
            width: 1px;
            height: 28px;
            background: #727C88;
            opacity: 0.2;
        }
    }

    .laptop{
        position: relative;
        left: 30px;
        z-index: 1;
        width: 88%;
        height: auto;
        margin-bottom: -140px;

        &:after {
            content: url(../../assets/img/shadow.png);
            position: absolute;
            bottom: -100px;
            left: 50%;
            transform: translateX(-50%);
        }

        &__img {
            width: 100%;
            height: auto;
            display: block;
            margin: 0 auto;
        }

        &__slider {
            position: absolute;
            z-index: 2;
            top: 2.4%;
            left: 50.4%;
            transform: translateX(-50%);
            width: 73%;
            height: 85%;
            border-radius: 15px 15px 0 0;
            overflow: hidden;
        }

        &__slide {
            width: 100% !important;
            height: 100% !important;
            background-repeat: no-repeat;
            background-position: center;
            background-size: cover;
            padding: 9% 0 0 7.5%;
        }

        &__buttons {
            display: flex;
            position: absolute;
            z-index: 2;
            left: 74px;
            top: calc(50% + 10px);
            transform: translateY(-50%);
        }

        &__btn {
            position: relative;
            display: flex;
            align-items: center;
            justify-content: center;

            &:hover {
                .progress-active {
                    opacity: 1;

                    &__circle {
                        stroke: #7FA3CA;
                        stroke-dasharray: 200;
                        animation: progress-btn .5s ease;
                    }
                }
            }

            &--next {
                margin-bottom: 6px;
            }

            &--prev {
                margin-right: 9px;
                margin-bottom: 6px;
            }
        }

        &__arrow {
            position: absolute;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
        }

        &__bullets {
            position: absolute;
            z-index: 2;
            left: 50%;
            transform: translateX(-50%);
            top: 63%;
            display: none;
            align-items: center;

            & > * {
                &:not(:last-child) {
                    // margin-right: 13px;
                }
            }

            /deep/ .swiper-pagination-bullet-active{
                // margin-right: 9px;
                width: 14px !important;
                height: 14px !important;
                border-radius: 50%;
                background: rgba(144, 184, 234, 0.25);
                position: relative;

                &:before {
                    content: '';
                    width: 6px;
                    height: 6px;
                    border-radius: 50%;
                    background: #618DC5;
                    opacity: 1;
                    position: absolute;
                    top: 50%;
                    left: 50%;
                    transform: translate(-50%,-50%);
                }
            }

            /deep/ .swiper-pagination-bullet{
                margin-right: 13px;
                width: 6px;
                height: 6px;
                border-radius: 50%;
                background: #90B8EA;
                opacity: 0.4;
                cursor: pointer;
            }
        }

        &__player {
            width: 62%;
            height: 23px;
            position: absolute;
            z-index: 3;
            bottom: 17.5%;
            left: calc(50% + 5px);
            transform: translateX(-50%);
            background: rgba(255,255,255, 0.2);
            border-radius: 5px;
            display: flex;
            align-items: center;
        }

        &__stop {
            width: 5%;
            // opacity: 1;
            transition: .3s all ease;
            pointer-events: all;
        }

        &__play {
            width: 5%;
            height: 100%;
            display: flex;
            align-items: center;
            justify-content: center;
            transition: .3s all ease;
            cursor: pointer;

            img {
                max-width: 10px;
                max-height: 10px;
            }
        }

        &__track {
            width: 90%;
            height: 1px;
            background: rgba(255, 255, 255, 0.12);
            position: relative;
            z-index: 1;

            &--active {
                position: absolute;
                height: 100%;
                top: 0;
                left: 0;
                z-index: 2;
                background: #fff;
            }
        }

        &__pagination {
            width: 5%;
            color: #fff;
            font-family: 'Roboto';
font-weight: normal;
            font-style: normal;
            font-weight: normal;
            font-size: 11px;
            line-height: 27px;
            opacity: 0.9;
            display: flex;
            justify-content: center;
            align-items: center;
        }

        &__title {
            font-family: 'SFProDisplay';
font-weight: 700;
            font-size: 48px;
            line-height: 63px;
            letter-spacing: 1.3px;
            text-transform: uppercase;
            color: #FFFFFF;
            margin-bottom: 2%; //15px
            max-width: 377px;
        }

        &__desc {
            font-family: 'Roboto';
font-weight: 300;
            font-style: normal;
            font-weight: 300;
            font-size: 15px;
            line-height: 25px;
            color: #FFFFFF;
            margin-bottom: 3.5%;
            max-width: 407px;
        }

        &__read-more {
            width: 146px;
            height: 43px;
            border-radius: 20px;
            background: linear-gradient(0deg, #FFFFFF, #FFFFFF), linear-gradient(189.87deg, #357FDB -14.01%, #2361AA 109.14%);
            display: flex;
            align-items: center;
            justify-content: center;
            font-family: 'SFProDisplay';
font-weight: 600;
            font-size: 11px;
            line-height: 19px;
            letter-spacing: 1px;
            text-transform: uppercase;
            color: #37393E;
            cursor: pointer;
            transition: .3s all ease;
            position: relative;

            span {
                position: relative;
                z-index: 2;
            }

            &:hover {
                background: transparent;

                &:before {
                    opacity: 1;
                }
            }

            &:before {
                content: '';
                width: 100%;
                height: 100%;
                border-radius: 20px;
                position: absolute;
                // z-index: 2;
                top: 0;
                left: 0;
                opacity: 0;
                // transition: .3s all ease;
                background: linear-gradient(0deg, rgba(255, 255, 255, 0.85), rgba(255, 255, 255, 0.85));
            }
        }
    }

    .social {
        display: flex;
        align-items: center;
        position: relative;
        z-index: 2;


        & > * {
            &:not(:last-child) {
                margin-right: 28px;
            }
        }

        &__item {
            svg {
                margin-bottom: -3px;

                path{
                    transition: .3s all ease;
                }
            }

            &:hover {
                svg.twitter,
                svg.facebook {
                    path {
                        fill: #4F7DB7;
                    }
                }

                svg.instagram {
                    .cls-2, .cls-1 {
                        fill: #4F7DB7;
                    }
                }
            }
        }
    }

    .small-screen {
        width: 355px;
        height: 267px;
        position: absolute;
        z-index: 3;
        top: 22%;
        right: 6%;
        // overflow: hidden;
        border-radius: 10px;

        &__header {
            height: 16px;
            width: 100%;
            background-color: #F7FAFF;
            display: flex;
            align-items: center;
            padding: 0 10px;
            border-radius: 10px 10px 0 0;

            & > * {
                &:not(:last-child) {
                    margin-right: 5px;
                }
            }
        }

        &__control {
            width: 5px;
            height: 5px;
            border-radius: 50%;

            &:nth-child(1) {
                background-color: #BACADF;
                opacity: 0.7;
            }

            &:nth-child(2) {
                background-color: #D4E1F2;
            }

            &:nth-child(3) {
                background-color: rgba(212, 225, 242, 0.5);
            }

            &:nth-child(4) {
                width: 35px;
                background: #DFE7F2;
                border-radius: 3px;
                margin-left: auto;
            }
        }

        &__img {
            border-radius: 0 0 10px 10px;
            overflow: hidden;
            width: 100%;
            height: 100%;

            img {
                width: 100%;
                height: 100%;
            }
        }
    }

    /deep/ .swiper-pagination-current{
        font-weight: bolder;
    }

    @media (max-width: 1200px) {
        .laptop {
            margin-bottom: -110px;

            &:after {
                transform: translateX(-50%) scale(0.7);
                bottom: -80px;
            }

            &__title {
                font-size: 32px;
                line-height: normal;
                max-width: 330px;
            }

            &__desc {
                max-width: 330px;
            }
        }

        .small-screen {
            transform: scale(0.8);
            top: 14%;
            right: 0;
        }
    }

    @media screen and (max-width: 960px) {
        .poster {
            height: 669px;

            &__top {
                padding: 0;
                height: 100%;
                border-radius: 12px 12px 0 0;
            }

            &__bottom {
                display: none;
            }

            &__social {
                display: none;
            }
        }

        .laptop {
            margin-bottom: 0;
            width: 100%;
            height: 100%;
            left: auto;

            &__img {
                height: 0;
            }

            &__slider {
                height: 669px;
                width: 100%;
                left: auto;
                top: auto;
                transform: none;
            }

            &__slide {
                padding: 80px 0 0 0;
            }

            &__title {
                max-width: 304px;
                margin: 0 auto 30px auto;
                text-align: center;
            }

            &__desc {
                max-width: 343px;
                margin: 0 auto 30px auto;
                text-align: center;
            }

            &__read-more {
                margin: 0 auto;
            }

            &__buttons {
                display: none;
            }

            &__bullets {
                display: flex;
            }

            &__player {
                display: none;
            }
        }

        .small-screen {
            // display: none;
            position: absolute;
            top: auto;
            bottom: -60px;
            left: 50%;
            transform: translateX(-50%);
            width: 90%;
            max-width: 373px;
            height: auto;

            &__slide {
                height: 100% !important;
                width: 100% !important;
            }
        }
    }

    @media (max-width: 640px) {
        .laptop {

            &__title {
                line-height: 42px;
                letter-spacing: 3.5px;
                margin-bottom: 19px;
            }

            &__desc {
                margin-bottom: 22px;
            }
        }

        .small-screen {
            width: 94%;
        }
    }
