.team {
    padding: 84px 0 100px 0;

    &__title {
        font-family: 'SFProDisplay-Bold';
        font-size: 36px;
        line-height: 56px;
        text-align: center;
        color: #37393E;
        margin-bottom: 33px;
    }

    &__subtitle {
        font-family: 'Roboto-Light';
        font-style: normal;
        font-weight: 300;
        font-size: 15px;
        line-height: 25px;
        text-align: center;
        color: #3D4950;
        opacity: 0.7;
        text-align: center;
        max-width: 484px;
        margin-left: auto;
        margin-right: auto;
        margin-bottom: 59px;
    }

    &__list {
        display: grid;
        grid-template-columns: repeat(5, 1fr);
        grid-column-gap: 9px;
        grid-row-gap: 9px;
    }

    &__item {
        width: 232px;
        height: 323px;
        background-repeat: no-repeat;
        background-position: center;
        background-size: cover;
        position: relative;
        transition: .3s all ease;

        &:hover {
            &:before {
                opacity: 0.7;
            }
        }

        &:before {
            content: '';
            width: 100%;
            height: 100%;
            position: absolute;
            z-index: 2;
            top: 0;
            left: 0;
            background: linear-gradient(165.38deg, #D55757 7.85%, #5EA3F8 105.07%);
            mix-blend-mode: lighten;
            opacity: 0;
            transition: .3s all ease;
        }
    }

    &__info {
        position: absolute;
        z-index: 2;
        left: 50%;
        transform: translateX(-50%);
        bottom: 10px;
    }

    &__name {
        font-family: 'SFProDisplay-Regular';
        font-size: 16px;
        line-height: 48px;
        text-align: center;
        color: #FFFFFF;
        margin-bottom: -10px;
    }

    &__position {
        font-family: 'Roboto-Light';
        font-style: normal;
        font-weight: 300;
        font-size: 12px;
        line-height: 20px;
        text-align: center;
        color: #FFFFFF;
    }
}
