.block {
    max-width: 676px;
    width: 100%;
    margin-bottom: 50px;
    position: relative;
    padding-bottom: 35.5%;

    &__poster {
        position: absolute;
        width: 100%;
        height: 100%;
    }

    &__btn {
        position: absolute;
        z-index: 2;
        left: 50%;
        top: 50%;
        transform: translate(-50%,-50%);
        width: 65px;
        height: 65px;
        border-radius: 50%;
        opacity: 0.6;
        border: 1px solid #FFFFFF;
        box-sizing: border-box;
        font-family: 'SFProDisplay-Regular';
        font-size: 10px;
        line-height: 19px;
        text-align: center;
        letter-spacing: 1px;
        text-transform: uppercase;
        color: #FFFFFF;
        transition: .5s all ease;

        &:after {
            content: '';
            position: absolute;
            z-index: 1;
            left: 50%;
            top: 50%;
            transform: translate(-50%,-50%);
            width: 100%;
            height: 100%;
            border-radius: 50%;
            opacity: 0.15;
            border: 5px solid #FFFFFF;
            transition: .5s all ease;
            animation-name: play;
            animation-duration: 1s;
            animation-iteration-count: infinite;
            animation-direction: alternate-reverse;
            animation-timing-function: ease;
        }
    }
}

@media (max-width: 1200px) {
    .block {
        max-width: 600px;
    }
}

@media (max-width: 960px) {
    .block {
        max-width: 100%;
        padding-bottom: 59%;
    }
}

@media (max-width: 640px) {
    .block {
        margin-bottom: 31px;
        padding-bottom: 67%;
    }
}

@keyframes play {
    0% {
        border: 5px solid #FFFFFF;
    }

    50% {
        border: 10px solid #fff;
    }

    100% {
        border: 5px solid #FFFFFF;
    }
}
