.carousel {
    width: 56.2%;
    height: 542px;
    position: absolute;
    top: 94px;
    right: 0;
}

.poster {
    background: linear-gradient(180deg, #EAF1FC -4.24%, #F8FAFF 86.01%), url('../../assets/img/application-poster-bg.png');
    position: relative;
    margin: 10px 10px 0 10px;
    border-radius: 8px 8px 0 0 ;
    overflow: hidden;

    &__info {
        position: relative;
        padding: 105px 0 77px 0;
        display: flex;
        flex-direction: column;
    }

    &__head-mobile {
        transform: rotate(-90deg);
        position: absolute;
        z-index: 3;
        top: 200px;
        right: -74px;
        display: none;
        align-items: center;
        width: 170px;
        height: 42px;
        background: linear-gradient(145.3deg, #E8AA6B 2.7%, #F55A43 109.91%), #C4C4C4;
        border-radius: 5px 5px 0 0;
        padding: 0 12px;

        span {
            margin-left: 10px;
            font-size: 13px;
            line-height: 22px;
            font-family: 'Roboto-Regular';
            font-style: normal;
            font-weight: normal;
            color: #fff;
        }
    }

    &__head {
        display: flex;
        justify-content: flex-start;
        align-items: center;
        padding-left: 11px;
        position: relative;
        order: 1;
    }

    &__icon {
        width: 42px;
        height: 42px;
        border-radius: 50%;
        background: linear-gradient(145.3deg, #E8AA6B 2.7%, #F55A43 109.91%), #C4C4C4;
        opacity: 1;
        display: flex;
        align-items: center;
        justify-content: center;
        position: relative;

        &:after {
            content: '';
            width: 64px;
            height: 64px;
            border-radius: 50%;
            background: linear-gradient(145.3deg, #FFBC77 2.7%, #FE6C56 109.91%), #C4C4C4;
            opacity: 0.23;
            position: absolute;
            top: 50%;
            left: 50%;
            transform: translate(-50%,-50%);
        }
    }

    &__nomination {
        margin-left: 26px;
        font-family: 'Roboto-Regular';
        font-style: normal;
        font-weight: normal;
        font-size: 14px;
        line-height: 20px;
        color: rgba(61, 73, 80, 0.7);
    }

    &__hash {
        display: inline-block;
        font-family: 'SFProDisplay-Regular';
        font-size: 15px;
        line-height: 22px;
        color: #498CED;
        margin-top: 70px;
        order: 2;
    }

    &__title {
        font-family: 'SFProDisplay-Bold';
        font-size: 45px;
        line-height: 59px;
        letter-spacing: 1px;
        text-transform: uppercase;
        color: #3D4950;
        margin-bottom: 15px;
        max-width: 366px;
        order: 3;
    }

    &__subtitle {
        font-family: 'Roboto-Light';
        font-style: normal;
        font-weight: 300;
        font-size: 15px;
        line-height: 25px;
        color: #3D4950;
        opacity: 0.7;
        max-width: 402px;
        margin-bottom: 23px;
        order: 4;
    }

    &__link {
        width: 146px;
        height: 43px;
        background: linear-gradient(0deg, #357FDB, #357FDB), linear-gradient(189.87deg, #357FDB -14.01%, #2361AA 109.14%);
        border-radius: 100px;
        cursor: pointer;
        display: flex;
        align-items: center;
        justify-content: center;
        font-family: 'SFProDisplay-Regular';
        font-size: 11px;
        line-height: 19px;
        text-align: center;
        letter-spacing: 1px;
        text-transform: uppercase;
        color: #FFFFFF;
        margin-bottom: 94px;
        position: relative;
        transition: .3s all ease;
        order: 5;

        span {
            position: relative;
            z-index: 2;
        }

        &:before {
            content: '';
            width: 100%;
            height: 100%;
            border-radius: 100px;
            background: linear-gradient(0deg, #145EB9, #145EB9), linear-gradient(189.87deg, #357FDB -14.01%, #2361AA 109.14%);
            position: absolute;
            z-index: 1;
            top: 0;
            left: 0;
            transition: .3s all ease;
            opacity: 0;
        }

        &:hover:before {
            opacity: 1;
        }
    }

    &__bottom {
        max-width: 345px;
        border-top: 1px solid rgba(206, 219, 231, 0.38);
        padding-top: 27px;
        order: 6;
    }

    &__price {
        display: inline-block;
        font-family: 'SFProDisplay-Regular';
        font-size: 12px;
        line-height: 19px;
        letter-spacing: 1px;
        text-transform: uppercase;
        color: #3D4950;

        &-tag {
            width: 70px;
            height: 26px;
            background: rgba(53, 127, 219, 0.08);
            border-radius: 100px;
            display: inline-flex;
            align-items: center;
            justify-content: center;
            font-family: 'GothamPro-Regular';
            font-size: 12px;
            line-height: 20px;
            color: #357FDB;
            margin-left: 9px;
        }
    }
}

@media (max-width: 960px) {
    .carousel {
        position: relative;
        top: auto;
        left: auto;
        width: 80%;
        margin-right: auto;
        margin-left: auto;
        height: 375px;
    }

    .poster {
        padding-bottom: 70px;

        &__head {
            display: none;
        }

        &__head-mobile {
            display: flex;
        }

        &__info {
            text-align: center;
            padding-bottom: 58px;
        }

        &__hash  {
            margin-top: 0;
            margin-bottom: 11px;
            order: 1;
        }

        &__title {
            margin: 0 auto 22px auto;
            order: 2;
        }

        &__subtitle {
            margin-left: auto;
            margin-right: auto;
            max-width: 343px;
            text-align: center;
            margin-bottom: 30px;
            order: 4;
        }

        &__link {
            margin: 0 auto;
            order: 5;
        }

        &__bottom {
            margin: 0 auto 18px auto;
            padding-top: 0;
            border-top: none;
            order: 3;
        }
    }
}

@media (max-width: 640px) {
    .carousel {
        width: 96%;
        height: 230px;
    }

    .poster {

        &__title {
            font-size: 36px;
            line-height: 43px;
            max-width: 304px;
            margin-bottom: 11px;
        }

        &__price {
            display: none;

            &-tag {
                margin-left: 0;
            }
        }

        &__bottom {
            margin-bottom: 15px;
        }

        &__subtitle {
            margin-bottom: 21px;
        }

        &__head-mobile {
            top: 134px;
        }
    }
}
