.card {
    max-width: 676px;
    width: 100%;
    height: 306px;
    margin-bottom: 50px;
    position: relative;
    border-radius: 6px;
    text-align: center;

    &:before {
        content: '';
        position: absolute;
        z-index: 0;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        background-image: url('../../../assets/img/wave.png');
        background-repeat: no-repeat;
        background-position: center;
        background-size: cover;
    }

    &__content {
        position: relative;
        z-index: 2;
        width: 100%;
        height: 100%;
        padding: 37px;
    }

    &__avatar {
        width: 90px;
        height: 90px;
        margin: 0 auto 18px auto;

        img {
            width: 100%;
            height: 100%;
        }
    }

    &__hash {
        font-family: 'SFProDisplay';
        font-weight: normal;
        font-size: 15px;
        line-height: 22px;
        color: #59DDCD;
        margin-bottom: 5px;
    }

    &__title {
        font-family: 'SFProDisplay';
        font-weight: 600;
        // margin-bottom: 12px;
        font-size: 33px;
        line-height: 36px;
        letter-spacing: -0.6px;
        color: #fff;
    }

    &__link {
        width: 139px;
        height: 40px;
        background: #FFFFFF;
        box-sizing: border-box;
        border-radius: 100px;
        display: flex;
        align-items: center;
        justify-content: center;
        text-transform: uppercase;
        font-family: 'SFProDisplay';
        font-weight: 600;
        font-size: 10px;
        line-height: 19px;
        letter-spacing: 1px;
        color: #37393E;
        cursor: pointer;
        margin: 0 auto;
        // position: relative;
        position: absolute;
        left: 50%;
        bottom: 38px;
        transform: translateX(-50%);

        span {
            position: relative;
            z-index: 2;
        }

        &:hover {
            background: transparent;

            &:after {
                opacity: 1;
            }
        }

        &:after {
            content: '';
            width: 100%;
            height: 100%;
            border-radius: 100px;
            border: none;
            position: absolute;
            top: 0;
            left: 0;
            opacity: 0;
            background: linear-gradient(0deg, rgba(255, 255, 255, 0.85), rgba(255, 255, 255, 0.85));
            // transition: .3s all ease;
        }
    }
}

@media (max-width: 1200px) {
    .card {
        max-width: 600px;
    }
}

@media (max-width: 960px) {
    .card {
        max-width: 100%;
    }
}

@media (max-width: 640px) {
    .card {
        margin-bottom: 31px;
    }
}
