result:
.poster {
    background-repeat: no-repeat;
    background-position: center;
    background-size: cover;
    margin: 10px 10px 0 10px;
    border-radius: 8px 8px 0 0 ;
    padding: 157px 0 33px 0;

    &__content {
        max-width: 407px;
        color: #FFFFFF;
        padding-bottom: 40px;
        position: relative;

        &:after {
            content: '';
            position: absolute;
            left: 0;
            bottom: 0;
            width: 82.3%;
            height: 1px;
            background: rgba(255, 255, 255, 0.06);
        }
    }

    &__title {
        font-family: 'SFProDisplay-Bold';
        font-size: 45px;
        line-height: 59px;
        letter-spacing: 1px;
        text-transform: uppercase;
        color: #FFFFFF;
        margin-bottom: 17px;
    }

    &__desc {
        font-family: 'Roboto-Regular';
        font-style: normal;
        font-weight: 300;
        font-size: 15px;
        line-height: 25px;
        letter-spacing: -0.1px;
    }

    &__application {
        display: flex;
        align-items: center;
        max-width: 331px;
        margin-top: 33px;
        margin-bottom: 78px;
    }

    &__avatar {
        max-width: 58px;
        max-height: 58px;
        overflow: hidden;
        margin-right: 15px;
    }

    &__id {
        margin-bottom: 5px;
    }

    &__type {
        display: inline-block;
        font-family: 'Roboto-Regular';
        font-style: normal;
        font-weight: normal;
        font-size: 12px;
        line-height: 23px;
        color: #FFFFFF;
        opacity: 0.43;
    }

    &__name {
        font-family: 'SFProDisplay-Regular';
        font-size: 15px;
        line-height: 19px;
        color: #FFFFFF;
    }

    &__link {
        margin-left: auto;
        position: relative;

        &:hover {
            .progress-active {
                opacity: 1;

                &__circle {
                    stroke: #7FA3CA;
                    stroke-dasharray: 200;
                    animation: progress-btn .5s ease;
                }
            }
        }

        .progress circle {
            stroke: #fff;
            opacity: 0.23;
        }

        .progress-active .progress-active__circle {
            stroke: #fff;
            opacity: 0.6;
        }

        .progress-arrow path {
            stroke: #fff;
        }
    }
}

.social-block {
    justify-content: flex-end;
    margin-right: 72px;
}

@media (max-width: 960px) {
    .poster {
        padding-top: 132px;

        &__content {
            margin-left: auto;
            margin-right: auto;

            &:after {
                width: 100%;
            }
        }

        &__title {
            font-family: 'SFProDisplay-Bold';
            font-size: 36px;
            line-height: 43px;
            text-align: center;
            margin-bottom: 30px;
        }

        &__desc {
            font-size: 15px;
            line-height: 25px;
            text-align: center;
        }

        &__application {
            max-width: 407px;
            margin-left: auto;
            margin-right: auto;
        }
    }

    .social-block {
        justify-content: center;
        margin-right: 0;
    }
}

@media (max-width: 640px) {
    .poster {
        padding-top: 122px;
        padding-bottom: 24px;

        &__content {
            padding-bottom: 26px;
        }

        &__title {
            max-width: 304px;
            margin: 0 auto 18px auto;
        }

        &__desc {
            max-width: 343px;
        }

        &__application {
            max-width: 343px;
            margin-bottom: 76px;
        }
    }

    .social-block {
        display: flex;
        flex-direction: column;
        justify-content: center;

        &__share {

        }
    }
}
