.widget {
    widows: 58%;
    height: 85%;
    position: absolute;
    z-index: 0;
    right: -12.5%;
    top: 0;
}
.poster {
    background: linear-gradient(180deg, #EAF1FC -4.24%, #F8FAFF 86.01%), url('../../assets/img/application-poster-bg.png');
    position: relative;
    margin: 10px 10px 0 10px;
    border-radius: 8px 8px 0 0;
    padding: 157px 0 88px 0;
    overflow: hidden;

    &__title {
        font-family: 'SFProDisplay-Bold';
        font-size: 45px;
        line-height: 59px;
        letter-spacing: 1px;
        text-transform: uppercase;
        color: #3D4950;
        max-width: 411px;
        margin-bottom: 16px;
        letter-spacing: 0.9px;
        padding-left: 9px;

        span {
            color: rgba(53, 127, 219, 1);
        }
    }

    &__subtitle {
        font-family: 'Roboto-Regular';
        font-style: normal;
        font-weight: 300;
        font-size: 15px;
        line-height: 25px;
        color: #3D4950;
        opacity: 0.7;
        max-width: 402px;
        margin-bottom: 84px;
        padding-left: 9px;
        letter-spacing: -0.3px;
    }

    &__links {
        display: flex;
        align-items: center;
        justify-content: flex-start;
        padding-left: 9px;
    }

    &__select {
        display: none;
        max-width: 374px;
        margin: 0 auto;

        /deep/ .vs__dropdown-toggle,
        /deep/ .vs__dropdown-menu {
            background: #fff;
            border: none;
            color: #3D4950;
            box-shadow: 0px 4px 33px rgba(52, 59, 76, 0.04);
            font-family: 'SFProDisplay-Regular';
        }

