        // padding-top: 5px;

        // /deep/ .vs__dropdown-toggle {
        //     border: none !important;
        // }

        // /deep/ .vs__dropdown-menu {
        //     border: none;

        //     /deep/ li {
        //         font-family: 'SFProDisplay-Regular';
        //     }
        // }

        // /deep/ .vs__selected {
        //     font-family: 'SFProDisplay-Regular';
        //     font-size: 15px;
        //     line-height: 19px;
        //     color: #3D4950;
        //     margin: 0;
        //     padding-left: 22px;
        // }

        // /deep/ .vs__actions {
