    }

    &__link {
        font-family: 'SFProDisplay-Regular';
        font-size: 15px;
        line-height: 19px;
        color: #3D4950;
        margin-right: 36px;
        opacity: 0.45;
        transition: .3s all ease;
        position: relative;
        padding-bottom: 3px;

        &.active {
            opacity: 1;

            &:after {
                opacity: 1;
            }
        }

        &:after {
            content: '';
            width: 100%;
            height: 1px;
            position: absolute;
            z-index: 1;
            bottom: 0;
            left: 0;
            background: rgba(53, 127, 219, 0.16);
            opacity: 0;
            transition: .3s all ease;
        }

        &:before {
            content: '';
            width: 0;
            height: 1px;
            position: absolute;
            z-index: 1;
            bottom: 0;
            left: 0;
            background: rgba(53, 127, 219, 0.47);
            transition: 0.5s all ease;
            opacity: 0;
        }

        &.active:hover:before {
            width: 100%;
            opacity: 1;
        }
    }
}

@media (max-width: 960px) {
    .poster {
        padding: 110px 0 70px 0;

        &__title {
            text-align: center;
            margin-left: auto;
            margin-right: auto;
        }

        &__subtitle {
            text-align: center;
            margin-left: auto;
            margin-right: auto;
        }

        &__links {
            display: none;
        }

        .v-select {
            display: block;
        }
    }
}

@media (max-width: 640px) {
    .poster {
        padding-top: 104px;
        padding-bottom: 36px;

        &__title {
            font-size: 36px;
            line-height: 43px;
            margin-bottom: 18px;
        }

        &__subtitle {
            margin-bottom: 37px;
        }
    }

    .widget {
        right: -40%;
    }
}
