.card {
    background-repeat: no-repeat;
    background-position: center;
    background-size: cover;
    height: 388px;
    width: 100%;
    box-shadow: 0px 4px 30px rgba(52, 59, 76, 0.08);
    border-radius: 6px;
    display: block;
    overflow: hidden;
    position: relative;
    // background: linear-gradient(142.31deg, #FFBC77 6.92%, #FF8284 91.53%) !important;
    text-align: center;
    padding: 19px 0 88px 0;
    margin-bottom: 0;
    transition: .3s all ease;

    &:hover {
        box-shadow: 0px 4px 30px rgba(52, 59, 76, 0.13);
    }

    &__hash {
        font-family: 'SFProDisplay';
        font-weight: normal;
        font-size: 15px;
        line-height: 22px;
        // color: #59DDCD;
        display: inline-block;
        margin-bottom: 51px;
        // color:#EF3044;
    }

    &__content {
        color: #FFFFFF;
        max-width: 362px;
        margin-left: auto;
        margin-right: auto;
        // max-width: 360px;
    }

    &__title {
        font-family: 'SFProDisplay';
        font-weight: 600;
        font-size: 40px;
        line-height: 34px;
        margin-bottom: 16px;
    }

    &__desc {
        font-family: 'Roboto';
        font-weight: 300;
        font-style: normal;
        font-weight: 300;
        font-size: 14px;
        line-height: 22px;
        margin-bottom: 25px;
    }

    &__link {
        width: 139px;
        height: 40px;
        background: #FFFFFF;
        box-sizing: border-box;
        border-radius: 100px;
        display: flex;
        align-items: center;
        justify-content: center;
        text-transform: uppercase;
        font-family: 'SFProDisplay';
        font-weight: 600;
        font-size: 10px;
        line-height: 19px;
        letter-spacing: 1px;
        color: #37393E;
        cursor: pointer;
        margin: 0 auto;
        // position: relative;
        position: absolute;
        left: 50%;
        bottom: 88px;
        transform: translateX(-50%);

        span {
            position: relative;
            z-index: 2;
        }

        &:hover {
            background: transparent;

            &:after {
                opacity: 1;
            }
        }

        &:after {
            content: '';
            width: 100%;
            height: 100%;
            border-radius: 100px;
            border: none;
            position: absolute;
            top: 0;
            left: 0;
            opacity: 0;
            background: linear-gradient(0deg, rgba(255, 255, 255, 0.85), rgba(255, 255, 255, 0.85));
            // transition: .3s all ease;
        }
    }
}

@media (max-width: 640px) {
    .card {
        height: 400px;
        width: 100%;
        padding: 18px 44px 0 44px;

        &__hash {
            margin-bottom: 58px;
        }

        &__content {
            max-width: none;
            // padding: 0 44px;
            margin-left: auto;
            margin-right: auto;
        }

        &__title {
            font-size: 33px;
            line-height: 36px;
        }

        &__link {
            bottom: 80px;
        }
    }
}

@media (max-width: 375px) {
    .card {
        padding: 18px 20px 0 20px;
    }
}
