.block {
    max-width: 676px;
    margin-bottom: 50px;

    &__title {
        font-family: 'SFProDisplay-Bold';
        font-size: 36px;
        line-height: 56px;
        color: #37393E;
        margin-bottom: 10px;
        letter-spacing: -0.5px;
    }

    &__content {

        p {
            font-family: 'Roboto-Regular';
            font-style: normal;
            font-weight: 300;
            font-size: 15px;
            line-height: 25px;
            color: #37393E;
            opacity: 0.7;
            margin-bottom: 45px;
            letter-spacing: -0.1px;
            max-width: 640px;
        }
    }

    &__blockquote {
        display: block;
        position: relative;
        margin: 0 auto 48px auto;
        // max-width: 640px;

        blockquote {
            display: block;
            max-width: 487px;
            margin: 0 auto;
            font-family: 'Roboto-Regular';
            font-style: normal;
            font-weight: 500;
            font-size: 18px;
            line-height: 30px;
            text-align: center;
            color: #37393E;
            padding-right: 35px;
        }

        &:after {
            content: url('../../../assets/img/quotes.svg');
            position: absolute;
            left: 0;
            top: calc(50% + 5px);
            transform: translateY(-50%);
        }

        &:before {
            content: url('../../../assets/img/quotes.svg');
            position: absolute;
            right: 35px;
            top: calc(50% + 5px);
            transform: translateY(-50%) rotate(-180deg);
        }
    }
}

@media (max-width: 1200px) {
    .block {
        max-width: 600px;
    }
}

@media (max-width: 960px) {
    .block {
        max-width: 100%;

        &__content {
            p {
                max-width: 100%;
            }
        }

        &__title {
            font-size: 32px;
            line-height: 56px;
        }
    }
}

@media (max-width: 640px) {
    .block {
        margin-bottom: 31px;

        &__content {
            p {
                letter-spacing: 0.5px;
                margin-bottom: 25px;
            }
        }

        &__title {
            margin-bottom: 5px;
        }

        &__blockquote {
            margin: 0 auto 36px auto;

            &:before {
                top: 50%;
            }

            &:after {
                top: 50%;
            }
        }
    }
}
