.widget {
    widows: 58%;
    height: 85%;
    position: absolute;
    z-index: 0;
    right: -12.5%;
    top: 0;
}
.poster {
    background: linear-gradient(180deg, #EAF1FC -4.24%, #F8FAFF 86.01%), url('../../assets/img/application-poster-bg.png');
    position: relative;
    margin: 10px 10px 0 10px;
    border-radius: 8px 8px 0 0;
    padding: 157px 0 88px 0;
    // overflow: hidden;

    &__title {
        font-family: 'SFProDisplay-Bold';
        font-size: 45px;
        line-height: 59px;
        letter-spacing: 1px;
        text-transform: uppercase;
        color: #3D4950;
        max-width: 411px;
        margin-bottom: 16px;
        letter-spacing: 0.9px;
        padding-left: 9px;

        span {
            color: rgba(53, 127, 219, 1);
            position: relative;
            z-index: 2;
            display: inline-block;

            &:after {
                content: '';
                position: absolute;
                z-index: -1;
                bottom: 3px;
                left: 0;
                width: 100%;
                height: 16px;
                background: #F8FAFE;
                opacity: 0.8;
                border-radius: 3px;
            }
        }
    }

    &__subtitle {
        font-family: 'Roboto-Light';
        font-style: normal;
        font-weight: 300;
        font-size: 15px;
        line-height: 25px;
        color: #3D4950;
        opacity: 0.7;
        max-width: 402px;
        margin-bottom: 84px;
        padding-left: 5px;
        // letter-spacing: -0.3px;
    }

    &__links {
        display: flex;
        align-items: center;
        justify-content: flex-start;
        padding-left: 9px;
    }

    &__select {
        display: none;
        max-width: 374px;
        margin: 0 auto;
        height: 46px;

        /deep/ .vs__dropdown-toggle,
        /deep/ .vs__dropdown-menu {
            background: #fff;
            border: none;
            color: #3D4950;
            box-shadow: 0px 4px 33px rgba(52, 59, 76, 0.04);
            font-family: 'SFProDisplay-Regular';
        }

        /deep/ .vs__dropdown-menu {
            // padding: 0 22px;
            max-height: auto;
            height: auto;
            overflow: auto;
            overflow-y: auto;

            li {
                padding: 10px 20px;
            }
        }

        /deep/ .vs__selected {
            padding-left: 20px;
        }

        /deep/ .vs__search {
            color: #3D4950;

            &::-webkit-input-placeholder{
                color: #3D4950;
            }
            &::-moz-placeholder {
                color: #3D4950;
            }
            &:-moz-placeholder {
                color: #3D4950;
            }
            &:-ms-input-placeholder {
                color: #3D4950;
            }
        }

        /deep/ .vs__dropdown-toggle {
            display: flex;
            align-items: center;
        }

        /deep/ .vs__selected-options {
            width: 100%;
        }

        /deep/ .vs__actions {
            transition: .3s all ease;
            margin-left: auto;

            svg path {
                fill: #3D4950;
            }
        }

        /deep/ .vs__dropdown-toggle {
            height: 46px;
        }

        /deep/ .vs__search {
            padding-left: 20px;
        }

        /deep/ .vs__actions {
            padding-right: 20px;
        }
    }

    &__link {
        font-family: 'SFProDisplay-Regular';
        font-size: 15px;
        line-height: 19px;
        color: #3D4950;
        margin-right: 36px;
        opacity: 0.45;
        transition: .3s all ease;
        position: relative;
        padding-bottom: 3px;

        &.active {
            opacity: 1;

            &:after {
                opacity: 1;
            }
        }

        &:after {
            content: '';
            width: 100%;
            height: 1px;
            position: absolute;
            z-index: 1;
            bottom: 0;
            left: 0;
            background: rgba(53, 127, 219, 0.16);
            opacity: 0;
            transition: .3s all ease;
        }

        &:before {
            content: '';
            width: 0;
            height: 1px;
            position: absolute;
            z-index: 1;
            bottom: 0;
            left: 0;
            background: rgba(53, 127, 219, 0.47);
            transition: 0.5s all ease;
            opacity: 0;
        }

        &.active:hover:before {
            width: 100%;
            opacity: 1;
        }
    }
}

@media (max-width: 960px) {
    .poster {
        padding: 110px 0 70px 0;

        &__title {
            text-align: center;
            margin-left: auto;
            margin-right: auto;
        }

        &__subtitle {
            text-align: center;
            margin-left: auto;
            margin-right: auto;
        }

        &__links {
            display: none;
        }

        .v-select {
            display: block;
        }
    }
}

@media (max-width: 640px) {
    .poster {
        padding-top: 108px;
        padding-bottom: 36px;

        &__title {
            font-size: 36px;
            line-height: 43px;
            margin-bottom: 18px;
        }

        &__subtitle {
            margin-bottom: 37px;
        }
    }

    .widget {
        right: -40%;
    }
}
