result:
.card {
    background-repeat: no-repeat;
    background-position: center;
    background-size: cover;
    height: 405px;
    box-shadow: 0px 4px 30px rgba(52, 59, 76, 0.08);
    border-radius: 6px;
    display: block;
    overflow: hidden;
    position: relative;
    // width: 66.05%;
    width: 790px;
    padding: 20px 65px 54px 65px;
    background: linear-gradient(139.57deg, #3B7FD4 7.57%, #0952A7 93.26%) !important;
    transition: .3s all ease;

    &:hover {
        box-shadow: 0px 4px 30px rgba(52, 59, 76, 0.13);
    }

    &__hash {
        font-family: 'SFProDisplay-Regular';
        font-size: 15px;
        line-height: 22px;
        color: #59DDCD;
        margin-bottom: 94px;
        display: inline-block;
    }

    &__content {
        color: #FFFFFF;
        max-width: 362px;
    }

    &__title {
        font-family: 'SFProDisplay-Semibold';
        font-size: 40px;
        line-height: normal;
        margin-bottom: 9px;
        letter-spacing: -0.7px;
    }

    &__desc {
        font-family: 'Roboto-Regular';
        font-style: normal;
        font-weight: 300;
        font-size: 14px;
        line-height: 22px;
        margin-bottom: 25px;
    }

    &__link {
        width: 139px;
        height: 40px;
        background: #FFFFFF;
        border: 1px solid rgba(255, 255, 255, 0.09);
        box-sizing: border-box;
        border-radius: 100px;
        display: flex;
        align-items: center;
        justify-content: center;
        text-transform: uppercase;
        font-family: 'SFProDisplay-Regular';
        font-size: 10px;
        line-height: 19px;
        letter-spacing: 1px;
        color: #37393E;
        cursor: pointer;
        transition: .3s all ease;
        position: relative;

        span {
            position: relative;
            z-index: 2;
        }

        &:hover:after {
            opacity: 1;
        }

        &:after {
            content: '';
            width: 100%;
            height: 100%;
            border-radius: 100px;
            border: none;
            position: absolute;
            top: 0;
            left: 0;
            opacity: 0;
            background: linear-gradient(0deg, rgba(255, 255, 255, 0.85), rgba(255, 255, 255, 0.85)), linear-gradient(189.87deg, #357FDB -14.01%, #2361AA 109.14%);
            transition: .3s all ease;
        }
    }
}

@media (max-width: 1200px) {
    .card {
        width: 620px;
    }
}

@media (max-width: 960px) {
    .card {
        width: 100%;
    }
}

@media (max-width: 640px) {
    .card {
        height: 400px;
        padding: 19px 0 0 0;
        text-align: center;

        &__hash {
            margin-bottom: 57px;
        }

        &__content {
            max-width: 303px;
            margin-right: auto;
            margin-left: auto;
        }

        &__title {
            font-size: 33px;
            line-height: 40px;
            margin-bottom: 12px;
        }

        &__link {
            margin: 0 auto;
        }
    }
}
