result:
.widget {
    position: relative;
    z-index: 1;
    opacity: 0.2;
    overflow: hidden;
    width: 58%;
    height: 100%;
    right: 0;

    &:before {
        content: '';
        position: absolute;
        z-index: 2;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        background: linear-gradient(180deg,transparent 60%, #F8FAFF 100%);
    }

    &__wrapper {
        display: flex;
        position: absolute;
        top:50%;
        left: 50%;
        transform: translate(-50%,-50%) rotate(10deg);


        & > * {
            &:not(:last-child) {
                margin-right: 10px;
            }
        }
    }
}

.col {
    position: relative;
    transition: .3s all ease;
    will-change:transform;

    & > * {
        &:not(:last-child) {
            margin-bottom: 10px;
        }
    }

    &-1 {
        bottom: 500px;
        animation-name: col;
        animation-duration: 20s;
        animation-iteration-count: infinite;
        animation-timing-function: linear;
    }

    &-2 {
        bottom: 800px;
        animation: col;
        animation-delay: 0ms;
        animation-duration: 80s;
        animation-iteration-count: infinite;
        animation-timing-function: linear;
    }

    &-3 {
        bottom: 1000px;
        animation: col;
        animation-delay: 0ms;
        animation-duration: 50s;
        animation-iteration-count: infinite;
        animation-timing-function: linear;
    }
}

@keyframes col {
    from {
        transform: translate3d(0,0,1px);
    }
    to {
        transform: translate3d(0,50%,1px);
    }
}

@media (max-width: 1200px) {
    .widget {
        &__wrapper {
            transform: translate(-50%,-50%) scale(0.7) rotate(10deg);
        }
    }
}

@media (max-width: 960px) {
    .widget {
        width: 75%;
    }
}

@media (max-width: 640px) {
    .widget {
        width: 115%;
        right: -40%;

        &__wrapper {
            transform: translate(-50%, -50%) scale(0.65) rotate(7deg);
        }
    }
}
