.description {
    position: relative;
    padding-bottom: 97px;

    &__sidebar {
        position: absolute;
        top: 19px;
        right: 81px;

        & > * {
            &:not(:last-child) {
                margin-bottom: 23px;
            }
        }
    }
}

.social-block {
    margin-top: -17px;
    margin-bottom: 70px;
}

@media (max-width: 1200px) {
    .description {
        &__sidebar {
            right: 30px;
        }
    }
}

@media (max-width: 960px) {
    .description {
        padding-bottom: 60px;

        &__sidebar {
            position: relative;
            top: auto;
            right: auto;
            padding: 0 30px;
            text-align: center;
            display: flex;
        }
    }

    .social-block {
        margin: 24px auto 40px auto;
        justify-content: center;
    }
}

@media (max-width: 640px) {
    .description {
        padding-bottom: 46px;

        &__sidebar {
            padding: 0 20px;
        }
    }

    .card.card-app {
        width: 100%;
    }
}
