.award {
	position: relative;
	display: inline-block;
	margin-bottom: 95px;
	margin-left: 11px;
	&__text {
		position: absolute;
		top: 50%;
		left: 68px;
		transform: translateY(-50%);

		width: 91px;

		font-family: "Roboto-Regular";
		font-style: normal;
		font-weight: normal;
		font-size: 14px;
		line-height: 20px;

		color: #FFFFFF;
	}
	&__icon {
		position: relative;
		width: 42px;
		height: 42px;
		border-radius: 50%;
		// border: 11px solid rgba(#357FDB, .45);
		// box-sizing: border-box;
		background: linear-gradient(135deg, #83A5FC 0%,#3182E7 100%);
		// background: linear-gradient(147.83deg, #83A5FC 2.55%, #3182E7 207.56%), #3182E7;
		// display: flex;
		// align-items: center;
		// justify-content: center;
		// position: absolute;
		z-index: 2;
		// left: 50%;
		// top: -23px;
		// transform: translateX(-50%);
		img {
			position: absolute;
			top: 50%;
			left: 50%;
			transform: translate(-50%,-50%);
		}
		&:before {
			content: "";
			position: absolute;
			top: 50%;
			left: 50%;
			transform: translate(-50%,-50%);
			width: calc(100% + 22px);
			height: calc(100% + 22px);
			border-radius: 50%;
			background-color: rgba(#357FDB, .45);
		}
	}
}
.poster {
	position: relative;

	background-repeat: no-repeat;
	background-position: center;
	background-size: cover;
	margin: 10px 10px 0 10px;
	border-radius: 8px 8px 0 0 ;
	padding: 105px 0 33px 0;
	height: 630px;

	&__content {
		max-width: 490px;
		color: #FFFFFF;
		padding-bottom: 40px;
		position: relative;

		&:after {
			content: '';
			position: absolute;
			left: 0;
			bottom: 0;
			width: 82.3%;
			height: 1px;
			background: rgba(255, 255, 255, 0.06);
		}
	}

	&__title {
		position: relative;

		font-family: 'SFProDisplay-Bold';
		font-size: 45px;
		line-height: 59px;
		letter-spacing: 1px;
		text-transform: uppercase;
		color: #FFFFFF;
		margin-bottom: 23px;
	}

	&__subtitle {
		position: absolute;
		top: -39px;
		left: 2px;

		font-family: "SFProDisplay-Regular";
		font-size: 19px;
		line-height: 48px;

		color: #357FDB;
	}

	&__desc {
		font-family: 'Roboto-Regular';
		font-style: normal;
		font-weight: 300;
		font-size: 15px;
		line-height: 25px;
		letter-spacing: -0.1px;
		max-width: 424px;
	}

	&__application {
		display: flex;
		align-items: center;
		max-width: 331px;
		margin-top: 33px;
		margin-bottom: 78px;
	}

	&__avatar {
		max-width: 58px;
		max-height: 58px;
		overflow: hidden;
		margin-right: 15px;
	}

	&__id {
		margin-bottom: 5px;
	}

	&__type {
		display: inline-block;
		font-family: 'Roboto-Regular';
		font-style: normal;
		font-weight: normal;
		font-size: 12px;
		line-height: 23px;
		color: #FFFFFF;
		opacity: 0.43;
	}

	&__name {
		font-family: 'SFProDisplay-Regular';
		font-size: 15px;
		line-height: 19px;
		color: #FFFFFF;
	}

	&__link {
		margin-left: auto;
		position: relative;

		&:hover {
			.progress-active {
				opacity: 1;

				&__circle {
					stroke: #7FA3CA;
					stroke-dasharray: 200;
					animation: progress-btn .5s ease;
				}
			}
		}

		.progress circle {
			stroke: #fff;
			opacity: 0.23;
		}

		.progress-active .progress-active__circle {
			stroke: #fff;
			opacity: 0.6;
		}

		.progress-arrow path {
			stroke: #fff;
		}
	}
}

@media (max-width: 960px) {
	.poster {
		padding-top: 132px;

		&__content {
			margin-left: auto;
			margin-right: auto;

			&:after {
				width: 100%;
			}
		}

		&__title {
			font-family: 'SFProDisplay-Bold';
			font-size: 36px;
			line-height: 43px;
			text-align: center;
			margin-bottom: 30px;
		}

		&__desc {
			font-size: 15px;
			line-height: 25px;
			text-align: center;
		}

		&__application {
			max-width: 407px;
			margin-left: auto;
			margin-right: auto;
		}

		.wrapper {
			text-align: center;
		}
	}

	.award {
		display: none;
	}

	// .social-block {
	// 	justify-content: center;
	// 	margin-right: 0;
	// }
}

@media (max-width: 960px) {
	.poster {
		padding-top: 150px;
		padding-bottom: 24px;
		height: 592px;

		&__content {
			padding-bottom: 26px;
		}

		&__title {
			margin: 0 auto 18px auto;
		}

		&__subtitle {
			top: -30px;
			left: 50%;
			transform: translateX(-50%);
			font-size: 15px;
			line-height: 22px;
		}

		&__desc {
			max-width: 343px;
		}

		&__application {
			max-width: 343px;
			margin-bottom: 76px;
		}

		&__desc {
			max-width: none;
		}

		.wrapper {
			padding: 0 25px;
		}

	}

	/deep/ .social-block {
		&__share {
			display: block;
			text-align: center;
		}
		&_absolute {
			right: auto;
			left: 50%;
			bottom: 25px;
			transform: translateX(-50%);
		}
	}
}

@media (max-width: 640px) {
	.poster {
		&__title {
			max-width: 304px;
		}
	}
}
