.select {
        display: none;
        max-width: 374px;
        margin: 0 auto;
        height: 46px;

        /deep/ .vs__dropdown-toggle,
        /deep/ .vs__dropdown-menu {
            background: #fff;
            border: none;
            color: #3D4950;
            box-shadow: 0px 4px 33px rgba(52, 59, 76, 0.04);
            font-family: 'SFProDisplay';
            font-weight: normal;
        }

        /deep/ .vs__dropdown-menu {
            // padding: 0 22px;
            max-height: auto;
            height: auto;
            overflow: auto;
            overflow-y: auto;

            li {
                padding: 10px 20px;
            }
        }

        /deep/ .vs__selected {
            padding-left: 20px;
        }

        /deep/ .vs__search {
            color: #3D4950;

            &::-webkit-input-placeholder{
                color: #3D4950;
                font-family: 'SFProDisplay';
                font-weight: 500;
                font-size: 15px;
                line-height: 19px;
            }
            &::-moz-placeholder {
                color: #3D4950;
            }
            &:-moz-placeholder {
                color: #3D4950;
            }
            &:-ms-input-placeholder {
                color: #3D4950;
            }
        }

        /deep/ .vs__dropdown-option {
            color: #3D4950;
            font-family: 'SFProDisplay';
            font-weight: 500;
            font-size: 15px;
            line-height: 19px;
        }

        /deep/ .vs__dropdown-toggle {
            display: flex;
            align-items: center;
        }

        /deep/ .vs__selected-options {
            width: 100%;
        }

        /deep/ .vs__actions {
            transition: .3s all ease;
            margin-left: auto;

            svg path {
                fill: #3D4950;
            }
        }

        /deep/ .vs__dropdown-toggle {
            height: 46px;
        }

        /deep/ .vs__search {
            padding-left: 20px;
        }

        /deep/ .vs__actions {
            padding-right: 20px;
            position: relative;
        }

        /deep/ .vs__open-indicator {
            position: relative;
            width: 10px;
            height: 6px;

            &:after {
                content: '';
                position: absolute;
                top: 0;
                left: 0;
                width: 100%;
                height: 100%;
                background-image: url('../assets/img/v-select-arrow.svg');
            }
        }
    }

@media (max-width: 960px) {
    .select {
        display: block;
    }
}
