.card {
    height: 450px;
    width: 353px;
    background: #FFFFFF;
    box-shadow: 0px 4px 30px rgba(52, 59, 76, 0.05);
    border-radius: 6px;
    padding: 16px 34px 32px 34px;
    transition: .3s all ease;
    overflow: hidden;

    &:hover {
        box-shadow: 0px 4px 30px rgba(52, 59, 76, 0.13);
    }

    &__title {
        font-family: 'SFProDisplay-Bold';
        font-size: 23px;
        line-height: 48px;
        color: #37393E;
        margin-bottom: 13px;
        letter-spacing: -0.5px;
    }

    &__field {
        padding-bottom: 17px;
        margin-bottom: 12px;
        position: relative;

        &:after {
            content: '';
            position: absolute;
            left: 0;
            bottom: 0;
            width: 100%;
            height: 1px;
            background: rgba(206, 219, 231, 0.26);
        }

        &:last-child:after {
            display: none;
        }
    }

    &__paragraph {
        display: inline-block;
        font-family: 'Roboto-Regular';
        font-style: normal;
        font-weight: normal;
        font-size: 12px;
        line-height: 23px;
        color: #37393E;
        opacity: 0.43;
        margin-bottom: 1px;
    }

    &__category {
        font-family: 'SFProDisplay-Regular';
        font-size: 15px;
        line-height: 19px;
        color: #37393E;
    }
}

@media (max-width: 1200px) {
    .card {
        width: 300px;
    }
}

@media (max-width: 960px) {
    .card {
        width: 47.5%;
        height: 415px;
        margin-left: 0;
        margin-right: auto;
    }
}

@media (max-width: 640px) {
    .card {
        width: 100%;
    }
}
