.block {
    max-width: 676px;
    margin-bottom: 50px;

    p {
        font-family: 'Roboto-Regular';
        font-style: normal;
        font-weight: 300;
        font-size: 15px;
        line-height: 25px;
        color: #37393E;
        opacity: 0.7;
        margin-bottom: 50px;
        letter-spacing: -0.1px;
    }
}

@media (max-width: 1200px) {
    .block {
        max-width: 600px;
    }
}

@media (max-width: 960px) {
    .block {
        max-width: 100%;

        p {
            margin-bottom: 40px;
        }
    }
}

@media (max-width: 640px) {
    .block {
        margin-bottom: 31px;

        p {
            letter-spacing: 0.6px;
            margin-bottom: 40px;
        }
    }
}
