.description {
	position: relative;
	padding-bottom: 97px;

	.wrapper {
		display: flex;
		justify-content: space-between;
		flex-wrap: nowrap;
	}

	&__sidebar {
		display: inline-block;
		margin-right: -40px;
		width: 353px;

		& > * {
			&:not(:last-child) {
				margin-bottom: 23px !important;
			}
		}
	}

	&__content {
		width: 100%;
		max-width: 676px;
	}
}

.social-block {
	margin-top: -17px;
	margin-bottom: 70px;
}

@media (max-width: 1285px) {
	.description {

		&__content {
			margin-right: 20px;

			& > * {
				margin-right: 20px;
			}
		}

		&__sidebar {
			margin-right: 0;
		}
	}
}

@media (max-width: 1200px) {
	.description {
		&__sidebar {
			// right: 30px;
			width: 300px;
		}
	}
}

@media (max-width: 960px) {
	.description {
		padding-bottom: 60px;

		.wrapper {
			display: block;
		}

		& > * {
			margin-right: 0;
		}

		&__sidebar {
			width: 100%;
			display: flex;
			flex-direction: row;
			flex-wrap: wrap;
			justify-content: space-between;

			& > * {
				margin-bottom: 23px !important;

				&:last-child {
					margin-right: 0;
				}
			}

			.specifications {
				height: auto;
			}
		}

		&__content {
			margin-right: 0;
			max-width: 100%;
		}
	}

	.social-block {
		margin: 24px auto 40px auto;
		justify-content: center;
	}
}

@media (max-width: 640px) {
	.description {
		padding-bottom: 46px;

		&__sidebar {

			& > * {
				width: 100%;
				margin-right: 0;
				margin-bottom: 20px;

				&:nth-child(odd) {
					margin-right: 0;
				}

				&:last-child {
					margin-right: 0;
				}
			}
		}
	}

	.card.card-app {
		width: 100%;
	}
}
